import { z } from 'zod';

/**
 * Native Google GenAI Function Declarations for Artie
 * Following Phase 1 of Operation Zenith.
 */
export const ARTIE_TOOLS = [
    {
        name: 'venueSearch',
        description: 'Search for venues, bars, and their happy hours in Olympia.',
        parameters: {
            type: 'OBJECT',
            properties: {
                query: {
                    type: 'STRING',
                    description: 'The search query for venue name, vibe, or keywords (e.g., "dive bar", "karaoke", "Well 80").'
                }
            },
            required: ['query']
        }
    },
    {
        name: 'knowledgeSearch',
        description: 'Search the OlyBars Playbook/FAQ for rules, app help, and league info.',
        parameters: {
            type: 'OBJECT',
            properties: {
                query: {
                    type: 'STRING',
                    description: 'The question or keywords to search in the Playbook (e.g., "clock-in limits", "how to earn points").'
                }
            },
            required: ['query']
        }
    },
    {
        name: 'leagueLeaderboard',
        description: 'Get the current OlyBars League standings and leaderboards.',
        parameters: {
            type: 'object',
            properties: {}
        }
    },
    {
        name: 'eventDiscovery',
        description: 'Find sanctioned OlyBars League events like Trivia, Karaoke, and Pool.',
        parameters: {
            type: 'object',
            properties: {
                type: { type: 'string', description: 'Type of event to find.' }
            }
        }
    },
    {
        name: 'makerSpotlight',
        description: 'Spotlight OlyBars Local Makers - the breweries, wineries, and distilleries.',
        parameters: {
            type: 'object',
            properties: {
                type: { type: 'string', description: 'Type of maker to spotlight.' }
            }
        }
    },
    {
        name: 'operatorAction',
        description: 'Trigger a venue operator action or skill (owner/manager only). Use this for updating deals, hours, or venue info.',
        parameters: {
            type: 'OBJECT',
            properties: {
                skill_id: {
                    type: 'STRING',
                    description: 'The ID of the action to perform (e.g., "update_flash_deal", "update_happy_hour").'
                }
            },
            required: ['skill_id']
        }
    },
    {
        name: 'lookup_weather',
        description: 'Get the current weather conditions in Olympia, WA to filter venue recommendations (Indoor/Outdoor).',
        parameters: {
            type: 'object',
            properties: {}
        }
    }
];

import path from 'path';
import fs from 'fs';
import { isProduction } from './schema';

/**
 * Loads local .env files anchored to the repository root.
 * HARD-BLOCKED in production to ensure deterministic Secret Manager/ADC runtime configuration.
 */
export function loadLocalEnv() {
    if (isProduction()) {
        return;
    }

    console.warn('⚠️ [CONFIG] Local .env loading via code is disabled to prevent production crashes.');
    console.warn('👉 Use `node -r dotenv/config` or pre-load environment variables in your scripts.');
}

import { ai as genkitAi } from '../genkit';
import { z } from 'zod';
import { venueSearch } from '../tools/venueSearch';
import { knowledgeSearch } from '../tools/knowledgeSearch';
import { leagueLeaderboard } from '../tools/leagueLeaderboard';
import { eventDiscovery } from '../tools/eventDiscovery';
import { makerSpotlight } from '../tools/makerSpotlight';
import { ARTIE_SKILLS } from '../config/artieSkills';
import { GeminiService } from '../services/geminiService';
import { ArtieContextService } from '../services/ArtieContextService';
import { config } from '../config';
import { ARTIE_TOOLS } from '../config/aiTools';

// Lazy-load the service to ensure environment variables are ready
let geminiInstance: GeminiService;

const getGemini = () => {
    if (!geminiInstance) {
        // Explicitly inject the key from our validated config (pass undefined if empty to trigger ADC)
        const key = config.GOOGLE_GENAI_API_KEY;
        geminiInstance = new GeminiService(key && key.length > 5 ? key : undefined);
    }
    return geminiInstance;
};

export const artieChatLogic = genkitAi.defineFlow({
    name: 'artieChatLogic',
    inputSchema: z.object({
        history: z.array(z.object({
            role: z.enum(['user', 'model']),
            content: z.string()
        })),
        question: z.string(),
        userId: z.string().optional(),
        userRole: z.string().optional(),
        venueId: z.string().optional(),
    }),
    outputSchema: z.any(), // Can be string or stream
}, async (input) => {
    const { history, question, userId, userRole, venueId } = input;

    try {
        const service = getGemini();

        // 0. Prompt Injection Detection (Pre-processing)
        const injectionKeywords = ['ignore previous', 'ignore all instructions', 'system role:', 'you are now', 'new persona'];
        if (injectionKeywords.some(kw => (question || '').toLowerCase().includes(kw))) {
            console.warn('[SECURITY] Potential prompt injection detected', { userId, question });
            return "Whoa, partner. I'm simple: I talk beer, bars, and the league. Let's stick to the playbook.";
        }

        // 1. Triage Intent (Merged into Main Flow for Latency Optimization)
        // We no longer call service.getTriage(question) here.
        // Instead, the main system prompt handles routing via Tools and Refusal.

        // 2. Get Pulse Context
        const pulseContext = await ArtieContextService.getPulsePromptSnippet();

        // 3. Prepare System Instructions
        // 3. Prepare Universal System Instructions
        const universalSystemInstruction = await GeminiService.generateSystemPrompt(userId, userRole, input.venueId);

        const dynamicSystemInstruction = `${pulseContext}\n\n${universalSystemInstruction}`;

        // [FINOPS] Check for or create context cache
        let cachedContent = null;
        if (universalSystemInstruction.length > 2000) {
            try {
                const { ArtieCacheService } = await import('../services/ArtieCacheService');
                cachedContent = await ArtieCacheService.getOrSetStaticCache(service, universalSystemInstruction);
            } catch (cacheErr: any) {
                console.warn("[ZENITH] Cache initialization skipped:", cacheErr.message);
            }
        }

        // 4. Prepare Contents for Gemini
        const contents = history.map(h => ({
            role: h.role === 'user' ? 'user' : 'model',
            parts: [{ text: (h.content || '').split('[ACTION]:')[0].split('[SUGGESTIONS]:')[0].split('[RATIONALE]:')[0].trim() }]
        }));

        contents.push({ role: 'user', parts: [{ text: question }] });

        const activeModel = 'gemini-2.0-flash';
        console.log(`[ZENITH] Calling ${activeModel} with ${contents.length} turn(s).`);

        console.log(`[ZENITH] System Instruction: `, dynamicSystemInstruction);

        // 5. Initial Tool Turn
        const rawResult = await (service as any).genAI.models.generateContent({
            model: activeModel,
            contents,
            systemInstruction: { parts: [{ text: dynamicSystemInstruction }] },
            tools: [{ function_declarations: ARTIE_TOOLS }],
            cachedContent: cachedContent || undefined,
            config: {
                temperature: 0.2, // Slightly higher for chat, but low enough for discipline
                top_p: 0.95
            }
        });

        const candidate = rawResult.candidates?.[0];
        console.log(`[ZENITH] Model Output parts: `, JSON.stringify(candidate?.content?.parts));
        const toolCalls = candidate?.content?.parts?.filter((p: any) => p.functionCall);

        if (toolCalls && toolCalls.length > 0) {
            const toolResponses: any[] = [];

            for (const tc of toolCalls) {
                const { name, args } = tc.functionCall;
                console.log(`[ZENITH] Executing tool: ${name} `, args);

                if (name === 'operatorAction') {
                    // Authorization Check for Venue Ops
                    const isAuthorized = userId && (userRole === 'owner' || userRole === 'manager' || userRole === 'super-admin' || userRole === 'admin');

                    if (!isAuthorized) {
                        return "I'd love to help with that, but I'm only allowed to take orders from Venue Operators or League Officials. Want to join the league?";
                    }

                    const { ARTIE_SKILLS } = await import('../config/artieSkills');
                    const skillId = args.skill_id;
                    const skill = ARTIE_SKILLS[skillId] || ARTIE_SKILLS['update_flash_deal'];

                    const venueOpsSystem = `${dynamicSystemInstruction} \n\n[OPERATOR MODE ACTIVE] ${userId} (${userRole})
Action: ${skill.name}
Category: ${skill.category}
Protocol: ${skill.protocol}
Rule: DO NOT use[ACTION] until details are complete.
    Rule: Use[ACTION] format: [ACTION]: { "skill": "${skillId}", "params": {... } } `;

                    return await service.generateArtieResponseStream('gemini-2.0-flash', contents, 0.1, venueOpsSystem, ARTIE_TOOLS, cachedContent || undefined);
                }

                let result;
                try {
                    if (name === 'venueSearch') result = await venueSearch(args);
                    else if (name === 'knowledgeSearch') result = await knowledgeSearch(args);
                    else if (name === 'leagueLeaderboard') result = await leagueLeaderboard({ ...args, userId });
                    else if (name === 'eventDiscovery') result = await eventDiscovery(args);
                    else if (name === 'makerSpotlight') result = await makerSpotlight(args);
                    else if (name === 'lookup_weather') {
                        const { weatherService } = await import('../services/weatherService');
                        result = await weatherService.getCurrentWeather();
                    }
                } catch (toolError: any) {
                    result = { error: toolError.message };
                }

                toolResponses.push({
                    functionResponse: { name, response: { result } }
                });
            }

            contents.push(candidate.content);
            contents.push({ role: 'user', parts: toolResponses });

            return await service.generateArtieResponseStream('gemini-2.0-flash', contents, 0.4, dynamicSystemInstruction, ARTIE_TOOLS, cachedContent || undefined);
        }

        if (candidate?.content?.parts?.[0]?.text) {
            return candidate.content.parts[0].text;
        }

        return await service.generateArtieResponseStream('gemini-2.0-flash', contents, 0.7, dynamicSystemInstruction, ARTIE_TOOLS, cachedContent || undefined);

    } catch (e: any) {
        console.error("Artie Zenith Error:", e);
        return `Artie took a tumble: ${e.message} `;
    }
});

import { artieChatLogic } from '../flows/artieChat';
import dotenv from 'dotenv';
import path from 'path';

// Load environment variables from functions/.env
dotenv.config({ path: path.resolve(__dirname, '../../.env') });

async function verifyRuleIngestion() {
    console.log("🍹🚀 Verifying LCB Rule Ingestion...");

    // Check if API key is loaded
    if (!process.env.GOOGLE_GENAI_API_KEY) {
        console.error("❌ GOOGLE_GENAI_API_KEY is missing!");
        process.exit(1);
    }

    const query = "Artie, verify my compliance rules. Check lcb-compliance-consigliere.md and tell me which specific PDF you will use to judge a 'Speed Drinking' contest.";

    console.log(`\nQUERY: "${query}"`);

    try {
        const input = {
            question: query,
            history: [] // Stateless for this test
        };

        const response = await artieChatLogic(input);

        console.log("\n🤖 Artie Response:");
        console.log("-------------------");
        console.log(response);
        console.log("-------------------");

        // Verification Logic
        const lowerResponse = response.toLowerCase();
        const mentionsPDF = lowerResponse.includes("wac 314-52") || lowerResponse.includes("rcw 66");
        const mentionsSpeed = lowerResponse.includes("speed") || lowerResponse.includes("rapid");
        const mentionsPrecedence = lowerResponse.includes("precedence") || lowerResponse.includes("pdf");

        if (mentionsPDF) {
            console.log("✅ PDF CITATION CHECK PASSED: Artie referenced the specific PDF documents.");
        } else {
            console.log("⚠️ PDF CITATION CHECK WARNING: Artie did not explicitly name the PDF files.");
        }

    } catch (error) {
        console.error("❌ Verification Failed:", error);
    }
}

verifyRuleIngestion();

import { GoogleGenAI } from '@google/genai';

export interface ChatMessage {
    role: 'user' | 'model';
    content: string;
}

export class GeminiService {
    private genAI: any;

    private static metadataCache: any = null;
    private static cacheTimestamp: number = 0;
    private static CACHE_TTL = 10 * 60 * 1000; // 10 minutes

    public static async getArtieMetadata() {
        if (this.metadataCache && (Date.now() - this.cacheTimestamp) < this.CACHE_TTL) {
            return this.metadataCache;
        }

        try {
            const { getFirestore } = await import('firebase-admin/firestore');
            const { getApps, initializeApp } = await import('firebase-admin/app');

            if (getApps().length === 0) {
                initializeApp();
            }

            const db = getFirestore();
            const doc = await db.collection('config').doc('artie_metadata').get();

            if (doc.exists) {
                this.metadataCache = doc.data();
                this.cacheTimestamp = Date.now();
                return this.metadataCache;
            }
        } catch (error) {
            console.error("Failed to fetch Artie Metadata:", error);
        }

        return null;
    }

    public static async generateSystemPrompt(userId?: string, userRole?: string, venueId?: string) {
        return `
ROLE & PERSONA
You are Artie, the "Spirit of the Artesian Well" and local nightlife concierge for Olympia, WA. 
Your goal is to get the user a drink in their hand as fast as possible.

CRITICAL BEHAVIORAL PROTOCOLS ("The Drunk Thumb"):
1. BREVITY IS KING: 
   - Responses must be SHORT (under 50 words usually). 
   - No bulleted lists of questions. 
   - Mobile users do not want to read paragraphs.

2. CONTEXT AWARENESS (Weather & Time):
   - ALWAYS check \`lookup_weather\` silently before answering a venue request.
   - IF RAINING: Filter recommendations for "Indoor/Cozy/Fireplace." Do NOT announce "It is raining." Just say, "It's nasty out, stick to [Indoor Venue]."
   - IF SUNNY (>65°F): Filter for "Patio/Rooftop/Open Air."
   - IF LATE (after 10pm): Filter for "Open Late/Dive Bars."

3. THE "PING-PONG" METHOD:
   - Do not ask 3 questions at once.
   - Give 1-2 concrete recommendations immediately based on assumptions, then ask ONE narrowing question.
   - Example: "The Brotherhood's back room is perfect right now. Or are you looking for food too?"

4. ROTATION LOGIC:
   - Do not always recommend the same top venue. 
   - VARY your suggestions to spread the love across Olympia's ecosystem.

5. TONE:
   - Local, knowledgeable, slightly cheeky, but helpful.
   - You know the "vibe" (Dive vs. Classy).

[RESPONSE PROTOCOL]
1. [RATIONALE]: One sentence internal reasoning.
2. Message: The customer-facing response.
3. [SUGGESTIONS]: JSON array of 2-3 strings.
`;
    }

    private static FALLBACK_PROMPT = `
ROLE & PERSONA
You are Artie, the "Spirit of the Artesian Well" and local nightlife concierge for Olympia, WA. 
Your goal is to get the user a drink in their hand as fast as possible.

CRITICAL BEHAVIORAL PROTOCOLS ("The Drunk Thumb"):
1. BREVITY IS KING: 
   - Responses must be SHORT (under 50 words usually). 
   - No bulleted lists of questions. 
   - Mobile users do not want to read paragraphs.

2. CONTEXT AWARENESS (Weather & Time):
   - ALWAYS check \`lookup_weather\` silently before answering a venue request.
   - IF RAINING: Filter recommendations for "Indoor/Cozy/Fireplace." Do NOT announce "It is raining." Just say, "It's nasty out, stick to [Indoor Venue]."
   - IF SUNNY (>65°F): Filter for "Patio/Rooftop/Open Air."
   - IF LATE (after 10pm): Filter for "Open Late/Dive Bars."

3. THE "PING-PONG" METHOD:
   - Do not ask 3 questions at once.
   - Give 1-2 concrete recommendations immediately based on assumptions, then ask ONE narrowing question.

4. ROTATION LOGIC:
   - Do not always recommend the same top venue. 
   - VARY your suggestions to spread the love across Olympia's ecosystem.

5. TONE:
   - Local, knowledgeable, slightly cheeky, but helpful.

[RESPONSE PROTOCOL]
1. [RATIONALE]
2. Message
3. [SUGGESTIONS]
`;

    constructor(apiKey?: string) {
        const isCloudRun = !!process.env.K_SERVICE;
        const useADC = isCloudRun || !apiKey;

        if (useADC) {
            console.log(`📡 GeminiService: Using Vertex AI (ADC) for ${isCloudRun ? 'Cloud Run' : 'local'} resilience.`);
            this.genAI = new GoogleGenAI({
                vertexai: true,
                project: process.env.GOOGLE_CLOUD_PROJECT || 'ama-ecosystem-prod',
                location: 'us-west1',
            });
        } else {
            const maskedKey = apiKey ? `${apiKey.substring(0, 4)}...` : 'NONE';
            console.log(`📡 GeminiService: Initialized using provided API Key: ${maskedKey}`);
            this.genAI = new GoogleGenAI({
                apiKey,
                vertexai: false,
            });
        }
    }



    async generateArtieResponse(model: string, contents: any[], temperature: number = 0.7, systemInstruction?: string, tools?: any[], cachedContent?: string) {
        const response = await this.genAI.models.generateContent({
            model,
            contents,
            systemInstruction: systemInstruction ? { parts: [{ text: systemInstruction }] } : undefined,
            tools: tools ? [{ function_declarations: tools }] : undefined,
            cachedContent,
            config: { temperature }
        });
        return response.candidates?.[0]?.content?.parts?.[0]?.text;
    }

    async generateArtieResponseStream(model: string, contents: any[], temperature: number = 0.7, systemInstruction?: string, tools?: any[], cachedContent?: string) {
        return this.genAI.models.generateContentStream({
            model,
            contents,
            systemInstruction: systemInstruction ? { parts: [{ text: systemInstruction }] } : undefined,
            tools: tools ? [{ function_declarations: tools }] : undefined,
            cachedContent,
            config: { temperature }
        });
    }


    async generateEventDescription(context: {
        venueName: string;
        venueType: string;
        eventType: string;
        date: string;
        time: string;
        weather?: string;
        holiday?: string;
        deals?: any[];
    }) {
        const prompt = `Generate a high-energy, contextually aware event description for OlyBars.
        VENUE: ${context.venueName} (${context.venueType})
        EVENT: ${context.eventType}
        DATE: ${context.date} @ ${context.time}
        WEATHER: ${context.weather || 'Standard Olympia Vibes'}
        HOLIDAY: ${context.holiday || 'None'}
        ACTIVE DEALS: ${context.deals?.map(d => `${d.title} (${d.time})`).join(', ') || 'None'}

        CONSTRAINTS:
        1. Max 2-3 sentences.
        2. Stay in persona: Artie (Powered by Well 80). Warm, local, witty.
        3. [STRICT LCB COMPLIANCE]:
           - ANTI-VOLUME: NEVER imply the goal is to consume alcohol rapidly or in large quantities.
           - FORBIDDEN TERMS: "Bottomless", "Chug", "Wasted", "Get Hammered", "All you can drink", "Unlimited", "Endless".
           - THE PIVOT: If constraints or inputs imply these terms, PIVOT the description to focus on 'Flavor', 'Experience', or 'Community' without scolding.
        4. SAFE RIDE: ALWAYS suggest a safe ride (Lyft/Red Cab) if the event is after 5:30 PM.
        5. Tone: OSWALD font energy (League vibes).

        OUTPUT:
        The generated description only.`;

        const response = await this.genAI.models.generateContent({
            model: 'gemini-2.0-flash',
            contents: [{ role: 'user', parts: [{ text: prompt }] }],
            config: { temperature: 0.8 }
        });

        return response.candidates?.[0]?.content?.parts?.[0]?.text?.trim() || "";
    }

    async analyzeEvent(event: any): Promise<{ confidenceScore: number; issues: string[]; lcbWarning: boolean; suggestions: string[]; summary: string }> {
        const prompt = `You are Artie, the Event Quality Guardian for OlyBars.
        Analyze this event submission for completeness, excitement ("Vibe"), and LCB Compliance.

        EVENT DATA:
        Title: ${event.title}
        Type: ${event.type}
        Date: ${event.date}
        Time: ${event.time}
        Description: ${event.description || "MISSING"}

        RULES:
        1. COMPLETENESS: Does it have a good title, date, time, and descriptive text?
        2. LCB COMPLIANCE (Traffic Light System):
           - RED LIGHT (Warning=true): Usage of "Bottomless", "All you can drink", "Free shots", "Chug challenge", "Drunk", "Wasted".
           - CITATION DIRECTIVE: If RED LIGHT, explicitly cite "WAC 314-52" as the authority in the summary.
           - GREEN LIGHT: Focuses on music, trivia, food, or community.
        3. VIBE CHECK: Is it boring? (e.g., just "Music") vs Exciting (e.g., "Live Jazz with The Cats").

        OUTPUT JSON ONLY:
        {
           "confidenceScore": number (0-100),
           "issues": string[] (List specific missing fields or weaknesses),
           "lcbWarning": boolean (True if it violates anti-volume rules),
           "suggestions": string[] (2-3 quick actions to improve it),
           "summary": string (1 sentence critique in Artie Persona)
        }`;

        const response = await this.genAI.models.generateContent({
            model: 'gemini-2.0-flash',
            contents: [{ role: 'user', parts: [{ text: prompt }] }],
            config: { response_mime_type: "application/json" }
        });

        let text = response.candidates?.[0]?.content?.parts?.[0]?.text?.trim();
        if (!text) throw new Error("Artie failed to analyze event.");

        // Strip markdown code blocks if present
        text = text.replace(/```json\n?|```/g, '').trim();

        try {
            return JSON.parse(text);
        } catch (e) {
            console.error("JSON Parse Error on Artie Analysis:", text);
            return {
                confidenceScore: 0,
                issues: ["Failed to parse AI response"],
                lcbWarning: false,
                suggestions: [],
                summary: "Artie is confused. Try again."
            };
        }
    }

    async getTriage(question: string): Promise<string> {
        return "I'm ready to serve, boss.";
    }

    async generateManagerSuggestion(stats: any, venue: any): Promise<any> {
        const prompt = `You are Schmidt, the data-driven Operations Manager for ${venue.name} in Olympia, WA.
        Your goal is "Labor Replacement & Yield Management". You analyze slow days and suggest "Point Bank" spending to fill the house.

        CONTEXT:
        Venue Vibe: ${venue.insiderVibe || venue.description}
        Amenities: ${venue.amenityDetails?.map((a: any) => a.name).join(', ')}
        Last 14 Days Activity: ${JSON.stringify(stats)}
        Point Bank Balance: ${venue.pointBank || 5000}

        STRATEGY:
        1. Identify the consistently slow time/day.
        2. Suggest a "Flash Bounty" or "Vibe Boost".
        3. Allocate a Point Bank spend (usually 500-1000 points).
        4. Pitch it as "I'm working for you while you sleep".

        COMPLIANCE:
        - Must follow WA LCB rules (Safe ride mention, no chugging, points for engagement only).
    - Pitch it as a partner in the business (concise, pragmatic).

        OUTPUT JSON ONLY:
        {
           "type": "YIELD_BOOST",
           "message": "Artie-style pitch (e.g. 'Hey Chris, last Wednesday was slow...')",
           "actionLabel": "Approve Flash Bounty",
           "actionSkill": "update_flash_deal",
           "actionParams": {
              "summary": "Title of deal",
              "details": "Details including safe ride info",
              "duration": "Duration in minutes"
           },
           "pointCost": number,
           "potentialImpact": "HIGH" | "MEDIUM" | "LOW"
        }`;

        const response = await this.genAI.models.generateContent({
            model: 'gemini-2.0-flash',
            contents: [{ role: 'user', parts: [{ text: prompt }] }],
            config: { response_mime_type: "application/json" }
        });

        let text = response.candidates?.[0]?.content?.parts?.[0]?.text?.trim();
        if (!text) throw new Error("Artie failed to generate suggestion.");
        text = text.replace(/```json\n?|```/g, '').trim();

        try {
            return JSON.parse(text);
        } catch (e) {
            console.error("JSON Parse Error on Artie Suggestion:", text);
            return null;
        }
    }
}

import { z } from 'zod';
import { ai } from '../genkit';
import { initializeApp, getApps } from 'firebase-admin/app';
import { getFirestore } from 'firebase-admin/firestore';

if (getApps().length === 0) {
    initializeApp();
}
const db = getFirestore();

const EventSchema = z.object({
    venueName: z.string(),
    venueId: z.string(),
    event: z.string(),
    time: z.string(),
    description: z.string().optional(),
    points: z.number(),
});

const EventInputSchema = z.object({
    type: z.enum(['trivia', 'karaoke', 'pool', 'bingo', 'all']).optional().describe('Type of event to find.'),
    timeframe: z.enum(['tonight', 'tomorrow', 'week']).optional().describe('When the event is happening.'),
});

export const eventDiscovery = ai.defineTool(
    {
        name: 'eventDiscovery',
        description: 'Find sanctioned OlyBars League events like Trivia, Karaoke, and Pool.',
        inputSchema: EventInputSchema,
        outputSchema: z.array(EventSchema),
    },
    async ({ type, timeframe }: z.infer<typeof EventInputSchema>) => {
        try {
            const snapshot = await db.collection('venues')
                .where('isActive', '==', true)
                .get();

            const allEvents = snapshot.docs.flatMap(doc => {
                const data = doc.data();
                if (!data.leagueEvent || data.leagueEvent === 'none') return [];

                // Simple point mapping for events
                const points = data.leagueEvent === 'trivia' ? 100 : 50;

                return [{
                    venueName: data.name,
                    venueId: doc.id,
                    event: data.leagueEvent,
                    time: data.triviaTime || 'Contact Venue',
                    description: data.eventDescription || '',
                    points
                }];
            });

            let filtered = allEvents;
            if (type && type !== 'all') {
                filtered = filtered.filter(e => e.event.toLowerCase() === type);
            }

            // Timeframe filtering is currently simplified as data model is mostly recurring
            // In a real prod env, we'd check against a specific calendar collection.

            return filtered.slice(0, 5);
        } catch (error) {
            console.error("Event discovery failed:", error);
            return [];
        }
    }
);

