export class NotificationService {
    /**
     * Dispatches a Pulse Alert to users who have starred the venue.
     * Fires only when status transitions to 'packed'.
     */
    static async dispatchPulseAlert(venueId: string, venueName: string) {
        try {
            console.log(`[NotificationService] Dispatching Pulse Alert for ${venueName} (${venueId})`);

            // 1. Find users who have favorited this venue
            const usersSnapshot = await db.collection('users')
                .where('favorites', 'array-contains', venueId)
                .where('sms_opt_in', '==', true)
                .get();

            if (usersSnapshot.empty) {
                console.log(`[NotificationService] No eligible users for ${venueName}`);
                return;
            }

            const now = Date.now();
            const pacificTime = new Date().toLocaleString("en-US", { timeZone: "America/Los_Angeles" });
            const currentHour = new Date(pacificTime).getHours();
            const currentMinute = new Date(pacificTime).getMinutes();
            const currentTimeStr = `${currentHour.toString().padStart(2, '0')}:${currentMinute.toString().padStart(2, '0')}`;

            for (const userDoc of usersSnapshot.docs) {
                const user = userDoc.data() as UserProfile;

                if (await this.shouldSendNotification(user, venueId, currentTimeStr, now)) {
                    await this.sendSms(user, `OlyBars Intel: ${venueName} is PACKED! The party is peaking. Standing room only! 🍻`);

                    // Update last notified timestamp
                    await userDoc.ref.update({
                        [`last_notified_at.${venueId}`]: now
                    });
                }
            }
        } catch (error) {
            console.error('[NotificationService] Error dispatching pulse alert:', error);
        }
    }

    /**
     * Checks if a notification should be sent based on hierarchy and cooldowns.
     */
    private static async shouldSendNotification(user: UserProfile, venueId: string, currentTimeStr: string, now: number): Promise<boolean> {
        // 1. Global SMS Opt-in (Redundancy check)
        if (!user.sms_opt_in) return false;

        // 2. Master Pulse Alert Toggle
        if (user.notificationSettings?.allow_pulse_alerts === false) return false;

        // 3. Quiet Hours Check (PST)
        const start = user.notificationSettings?.quiet_hours_start || "23:00";
        const end = user.notificationSettings?.quiet_hours_end || "08:00";

        if (this.isWithinQuietHours(currentTimeStr, start, end)) {
            console.log(`[NotificationService] Skipping user ${user.uid} - within quiet hours (${start}-${end})`);
            return false;
        }

        // 4. Cooldown Check (4 Hours per Venue)
        const lastNotified = user.last_notified_at?.[venueId] || 0;
        const fourHoursInMs = 4 * 60 * 60 * 1000;
        if (now - lastNotified < fourHoursInMs) {
            console.log(`[NotificationService] Skipping user ${user.uid} - cooldown active for ${venueId}`);
            return false;
        }

        return true;
    }

    /**
     * Logic to handle cross-midnight quiet hours.
     */
    private static isWithinQuietHours(current: string, start: string, end: string): boolean {
        if (start < end) {
            return current >= start && current <= end;
        } else {
            // e.g., 23:00 to 08:00
            return current >= start || current <= end;
        }
    }

    /**
     * Mock SMS Sender (Integration point for Twilio/SNS)
     */
    private static async sendSms(user: UserProfile, message: string) {
        if (!user.phone) {
            console.warn(`[NotificationService] User ${user.uid} opted in but has no phone number.`);
            return;
        }

        console.log(`\n📱 --- OUTGOING SMS ---`);
        console.log(`To: ${user.phone} (${user.handle || 'User'})`);
        console.log(`Message: ${message}`);
        console.log(`-----------------------\n`);

        // Real implementation would call Twilio here
        // await twilio.messages.create({ body: message, to: user.phone, from: 'OlyBars' });
    }

    /**
     * Dispatch specialized League Intel with "Cheat Code" formatting.
     */
    static async dispatchLeagueIntel(venueId: string, message: string, isCheatCode: boolean) {
        // Implement logic for event-based notifications
        console.log(`[NotificationService] Dispatching League Intel for ${venueId}: ${message} (CheatCode: ${isCheatCode})`);
    }
}

import { db } from '../firebaseAdmin.js';
import { Venue, PartnerTier, LeagueEvent } from '../../../src/types/venue';

/**
 * League Night Event Scraper Service
 * Responsible for background ingestion of events for premium partners.
 */
export class ScraperService {

    /**
     * Main task entry point: Fetches and processes all eligible venues.
     */
    static async runScheduledScrape() {
        console.log('[Scraper] Starting scheduled scrape job...');
        const venues = await this.getEligibleVenues();

        for (const venue of venues) {
            try {
                await this.processVenue(venue);
            } catch (error) {
                console.error(`[Scraper] Failed to process ${venue.name}:`, error);
            }
        }
    }

    /**
     * Get all active venues with PRO or AGENCY status and scraper enabled.
     */
    private static async getEligibleVenues(): Promise<Venue[]> {
        const snapshot = await db.collection('venues')
            .where('partnerConfig.tier', 'in', [PartnerTier.PRO, PartnerTier.AGENCY])
            .where('is_scraping_enabled', '==', true)
            .where('isActive', '==', true)
            .get();

        return snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as Venue));
    }

    /**
     * Orchestrates the 4-step flow for a single venue.
     */
    private static async processVenue(venue: Venue) {
        if (!venue.scrape_source_url) {
            console.warn(`[Scraper] No source URL for ${venue.name}. Skipping.`);
            return;
        }

        // 1. THE SCOUT (Fetcher) - Logic placeholder
        // TODO: Integrate Puppeteer to fetch HTML/Screenshots from venue.scrape_source_url
        const rawContent = `[MOCK_CONTENT] Trivia every Thursday at 7:00 PM at ${venue.name}`;

        // 2. THE BRAIN (LLM) - Logic placeholder
        // TODO: Send to OpenAI/Gemini Vision to parse events from rawContent
        const parsedEvents = [
            {
                title: 'Weekly Trivia',
                description: 'General knowledge trivia and craft beer.',
                type: 'trivia',
                startTime: this.nextDayOfWeek(venue, 'Thursday', 19), // Thursday at 7 PM
                pointsAwarded: 25,
                sourceConfidence: 0.95
            }
        ];

        // 3. THE NORMALIZER & GATEKEEPER
        for (const eventData of parsedEvents) {
            if (eventData.sourceConfidence < 0.8) {
                console.warn(`[Scraper] Low confidence (${eventData.sourceConfidence}) for event: ${eventData.title}. Skipping.`);
                continue;
            }

            await this.saveEvent(venue.id, eventData);
        }

        // Update last scrape timestamp
        await db.collection('venues').doc(venue.id).update({
            last_scrape_timestamp: Date.now()
        });
    }

    /**
     * Saves a parsed event to the LeagueEvents collection.
     */
    private static async saveEvent(venueId: string, data: any) {
        const eventId = `scraped_${venueId}_${data.startTime}`;
        const event: LeagueEvent = {
            id: eventId,
            venueId,
            ...data,
            lastScraped: Date.now()
        };

        // Upsert to prevent duplicates
        await db.collection('league_events').doc(eventId).set(event, { merge: true });
        console.log(`[Scraper] Synced event: ${data.title} for venue ${venueId}`);
    }

    /**
     * Helper to calculate the next occurrence of a day/time.
     */
    private static nextDayOfWeek(venue: Venue, day: string, hour: number): number {
        // Simplified mock logic for timestamp calculation
        const d = new Date();
        d.setHours(hour, 0, 0, 0);
        return d.getTime();
    }
}

﻿import { GoogleGenAI } from '@google/genai';
import { ARTIE_SYSTEM_INSTRUCTION } from '../config/agents/artie';
import { SCHMIDT_SYSTEM_INSTRUCTION } from '../config/agents/schmidt';

export interface ChatMessage {
    role: 'user' | 'model';
    content: string;
}

export class GeminiService {
    private genAI: any;

    // Agent Personas (Imported from Config)
    public static ARTIE_PERSONA = ARTIE_SYSTEM_INSTRUCTION;
    public static SCHMIDT_PERSONA = SCHMIDT_SYSTEM_INSTRUCTION;

    constructor(apiKey?: string) {
        const isCloudRun = !!process.env.K_SERVICE;
        const useADC = isCloudRun || !apiKey;

        if (useADC) {
            console.log(`📡 GeminiService: Using Vertex AI (ADC) for ${isCloudRun ? 'Cloud Run' : 'local'} resilience.`);
            this.genAI = new GoogleGenAI({
                vertexai: true,
                project: process.env.GOOGLE_CLOUD_PROJECT || 'ama-ecosystem-prod',
                location: 'us-west1',
            });
        } else {
            const maskedKey = apiKey ? `${apiKey.substring(0, 4)}...` : 'NONE';
            console.log(`📡 GeminiService: Initialized using provided API Key: ${maskedKey}`);
            this.genAI = new GoogleGenAI({
                apiKey,
                vertexai: false,
            });
        }
    }

    // Generic Generation Method (Used by Chat Routes)
    async generateArtieResponse(model: string, contents: any[], temperature: number = 0.7, systemInstruction?: string, tools?: any[], cachedContent?: string) {
        // Default to Artie if no instruction provided, but allow overrides
        const instruction = systemInstruction || GeminiService.ARTIE_PERSONA;

        const response = await this.genAI.models.generateContent({
            model,
            contents,
            systemInstruction: { parts: [{ text: instruction }] },
            tools: tools ? [{ function_declarations: tools }] : undefined,   
            cachedContent,
            config: { temperature }
        });
        return response.candidates?.[0]?.content?.parts?.[0]?.text;
    }

    async generateArtieResponseStream(model: string, contents: any[], temperature: number = 0.7, systemInstruction?: string, tools?: any[], cachedContent?: string) {
        const instruction = systemInstruction || GeminiService.ARTIE_PERSONA;

        return this.genAI.models.generateContentStream({
            model,
            contents,
            systemInstruction: { parts: [{ text: instruction }] },
            tools: tools ? [{ function_declarations: tools }] : undefined,   
            cachedContent,
            config: { temperature }
        });
    }

    async generateEventDescription(context: {
        venueName: string;
        venueType: string;
        eventType: string;
        date: string;
        time: string;
        weather?: string;
        holiday?: string;
        deals?: any[];
    }) {
        // Uses a specific mini-prompt for descriptions, keeping Artie's voice
        const prompt = `Generate a high-energy, contextually aware event description for OlyBars.
        VENUE: ${context.venueName} (${context.venueType})
        EVENT: ${context.eventType}
        DATE: ${context.date} @ ${context.time}
        WEATHER: ${context.weather || 'Standard Olympia Vibes'}
        HOLIDAY: ${context.holiday || 'None'}
        ACTIVE DEALS: ${context.deals?.map(d => `${d.title} (${d.time})`).join(', ') || 'None'}

        CONSTRAINTS:
        1. Max 2-3 sentences.
        2. Stay in persona: Artie (Powered by Well 80). Warm, local, witty.  
        3. [STRICT LCB COMPLIANCE]:
           - ANTI-VOLUME: NEVER imply the goal is to consume alcohol rapidly or in large quantities.
           - FORBIDDEN TERMS: "Bottomless", "Chug", "Wasted", "Get Hammered", "All you can drink", "Unlimited", "Endless".
           - THE PIVOT: If constraints or inputs imply these terms, PIVOT the description to focus on 'Flavor', 'Experience', or 'Community' without scolding.
        4. SAFE RIDE: ALWAYS suggest a safe ride (Lyft/Red Cab) if the event is after 5:30 PM.
        5. Tone: OSWALD font energy (League vibes).

        OUTPUT:
        The generated description only.`;

        const response = await this.genAI.models.generateContent({
            model: 'gemini-2.0-flash',
            contents: [{ role: 'user', parts: [{ text: prompt }] }],
            config: { temperature: 0.8 }
        });

        return response.candidates?.[0]?.content?.parts?.[0]?.text?.trim() || "";
    }

    async analyzeEvent(event: any): Promise<{ confidenceScore: number; issues: string[]; lcbWarning: boolean; suggestions: string[]; summary: string }> { 
        // Event Analysis uses Artie as the "Guardian"
        const prompt = `You are Artie, the Event Quality Guardian for OlyBars.
        Analyze this event submission for completeness, excitement ("Vibe"), and LCB Compliance.

        EVENT DATA:
        Title: ${event.title}
        Type: ${event.type}
        Date: ${event.date}
        Time: ${event.time}
        Description: ${event.description || "MISSING"}

        RULES:
        1. COMPLETENESS: Does it have a good title, date, time, and descriptive text?
        2. LCB COMPLIANCE (Traffic Light System):
           - RED LIGHT (Warning=true): Usage of "Bottomless", "All you can drink", "Free shots", "Chug challenge", "Drunk", "Wasted".
           - CITATION DIRECTIVE: If RED LIGHT, explicitly cite "WAC 314-52" as the authority in the summary.
           - GREEN LIGHT: Focuses on music, trivia, food, or community.      
        3. VIBE CHECK: Is it boring? (e.g., just "Music") vs Exciting (e.g., "Live Jazz with The Cats").

        OUTPUT JSON ONLY:
        {
           "confidenceScore": number (0-100),
           "issues": string[] (List specific missing fields or weaknesses),  
           "lcbWarning": boolean (True if it violates anti-volume rules),    
           "suggestions": string[] (2-3 quick actions to improve it),        
           "summary": string (1 sentence critique in Artie Persona)
        }`;

        const response = await this.genAI.models.generateContent({
            model: 'gemini-2.0-flash',
            contents: [{ role: 'user', parts: [{ text: prompt }] }],
            config: { response_mime_type: "application/json" }
        });

        let text = response.candidates?.[0]?.content?.parts?.[0]?.text?.trim();
        if (!text) throw new Error("Artie failed to analyze event.");        

        text = text.replace(/```json\n?|```/g, '').trim();

        try {
            return JSON.parse(text);
        } catch (e) {
            console.error("JSON Parse Error on Artie Analysis:", text);      
            return {
                confidenceScore: 0,
                issues: ["Failed to parse AI response"],
                lcbWarning: false,
                suggestions: [],
