export const AssetToggleGrid: React.FC<AssetToggleGridProps> = ({ selectedAssets, onChange }) => {
    const categories: ('Games' | 'Entertainment' | 'Atmosphere')[] = ['Games', 'Entertainment', 'Atmosphere'];

    return (
        <div className="space-y-8">
