import React, { useEffect, useRef, useState } from 'react';
import { Venue } from '../../../types';
import { Loader2, MapPin, Save, X } from 'lucide-react';
import { useGoogleMapsScript } from '../../../hooks/useGoogleMapsScript';

interface PinCalibrationMapProps {
    venue: Venue;
    onSave: (coords: { lat: number; lng: number }) => void;
    onCancel: () => void;
}

export const PinCalibrationMap: React.FC<PinCalibrationMapProps> = ({ venue, onSave, onCancel }) => {
    const mapRef = useRef<HTMLDivElement>(null);
    const { status } = useGoogleMapsScript();
    const [map, setMap] = useState<google.maps.Map | null>(null);
    const [marker, setMarker] = useState<google.maps.marker.AdvancedMarkerElement | null>(null);
    const [currentCoords, setCurrentCoords] = useState({
        lat: venue.location?.lat || 47.0425,
        lng: venue.location?.lng || -122.9007
    });

    useEffect(() => {
        if (status === 'ready' && mapRef.current && !map) {
            const initialMap = new (window as any).google.maps.Map(mapRef.current, {
                center: currentCoords,
                zoom: 18,
                mapId: '6b4fa3a2419c825a',
                disableDefaultUI: false,
                zoomControl: true,
                mapTypeControl: false,
                streetViewControl: true,
            });

            const advMarker = new (window as any).google.maps.marker.AdvancedMarkerElement({
                map: initialMap,
                position: currentCoords,
                title: `Drag to calibrate: ${venue.name}`,
                gmpDraggable: true,
            });

            advMarker.addListener('dragend', (event: any) => {
                const position = advMarker.position;
                if (position) {
                    setCurrentCoords({
                        lat: typeof position.lat === 'function' ? position.lat() : position.lat,
                        lng: typeof position.lng === 'function' ? position.lng() : position.lng
                    });
                }
            });

            setMap(initialMap);
            setMarker(advMarker);
        }
    }, [status, venue]);

    return (
        <div className="flex flex-col h-[500px] w-full bg-slate-900 rounded-2xl overflow-hidden border border-white/10 shadow-2xl animate-in fade-in zoom-in duration-300">
            <div className="p-4 bg-slate-800 border-b border-white/5 flex justify-between items-center shrink-0">
                <div>
                    <h3 className="text-sm font-black text-white uppercase tracking-widest font-league flex items-center gap-2">
                        <MapPin className="w-4 h-4 text-primary" /> Pin Calibration: {venue.name}
                    </h3>
                    <p className="text-[10px] text-slate-400 font-bold uppercase mt-0.5 italic">Drag the marker to the front door</p>
                </div>
                <button onClick={onCancel} className="p-2 hover:bg-white/5 rounded-full text-slate-400 transition-colors">
                    <X className="w-5 h-5" />
                </button>
            </div>

            <div className="flex-1 relative">
                <div ref={mapRef} className="w-full h-full" />
                {status === 'loading' && (
                    <div className="absolute inset-0 flex items-center justify-center bg-slate-900/80 backdrop-blur-sm z-10">
                        <Loader2 className="w-8 h-8 text-primary animate-spin" />
                    </div>
                )}
            </div>

            <div className="p-4 bg-slate-800 border-t border-white/5 flex justify-between items-center shrink-0">
                <div className="flex gap-4 font-mono text-[10px]">
                    <div>
                        <span className="text-slate-500 uppercase mr-1">Lat:</span>
                        <span className="text-primary font-bold">{currentCoords.lat.toFixed(6)}</span>
                    </div>
                    <div>
                        <span className="text-slate-500 uppercase mr-1">Lng:</span>
                        <span className="text-primary font-bold">{currentCoords.lng.toFixed(6)}</span>
                    </div>
                </div>
                <button
                    onClick={() => onSave(currentCoords)}
                    className="bg-primary text-black font-black px-6 py-2 rounded-xl text-[10px] uppercase tracking-widest flex items-center gap-2 hover:scale-105 active:scale-95 transition-all shadow-xl"
                >
                    <Save className="w-3.5 h-3.5" /> Save Coordinates
                </button>
            </div>
        </div>
    );
};

import React, { useState } from 'react';
import { Venue } from '../../../types';
import { Check, X, AlertTriangle, Eye, EyeOff, ChevronRight, LayoutGrid, Info } from 'lucide-react';
import { updateVenueDetails } from '../../../services/venueService';
import { useQueryClient } from '@tanstack/react-query';

interface RefineryQueueProps {
    venues: Venue[];
}

export const RefineryQueue: React.FC<RefineryQueueProps> = ({ venues }) => {
    const queryClient = useQueryClient();
    const [expandedVenueId, setExpandedVenueId] = useState<string | null>(null);
    const [processingId, setProcessingId] = useState<string | null>(null);

    const drafts = venues.filter(v => v.ai_draft_profile?.status === 'needs_review');

    const handleApprove = async (venue: Venue) => {
        if (!venue.ai_draft_profile) return;
        setProcessingId(venue.id);

        const draft = venue.ai_draft_profile;
        const isExempt = ['well-80', 'hannahs', 'brotherhood-lounge'].includes(venue.id);

        // --- MASTER MERGE PROTOCOL (OlyBars Standard) ---

        // 1. DRAFT BASICS (The new truth floor)
        const updatedVenue: Partial<Venue> = {
            name: draft.identity.name || venue.name,
            website: draft.identity.website_url || venue.website,
            facebook: draft.identity.social_links?.facebook || venue.facebook,
            instagram: draft.identity.social_links?.instagram || venue.instagram,

            // Pull in V7 Google Precision if available
            location: draft.metadata.location || venue.location,
            coordinates: draft.metadata.coordinates || venue.coordinates,

            // CLEANUP: Wipe the draft after successful merge
            ai_draft_profile: null as any
        };

        if (isExempt) {
            // TIER 1: CAUTIOUS ADDITIVE MERGE (Protected)
            updatedVenue.vibe = draft.vibe?.headline || venue.vibe;
            updatedVenue.insiderVibe = draft.vibe?.insider_tip || venue.insiderVibe;
            updatedVenue.sceneTags = Array.from(new Set([...(venue.sceneTags || []), ...(draft.vibe?.audience_tags || [])])) as any[];
            updatedVenue.hasOutdoorSeating = draft.features?.has_outdoor_seating ?? venue.hasOutdoorSeating;

            // Transform schedule to hours map if live is empty
            if (!venue.hours && draft.weekly_schedule?.length > 0) {
                updatedVenue.hours = draft.weekly_schedule.reduce((acc: any, day) => {
                    if (day.open_time && day.close_time) {
                        acc[day.day] = { open: day.open_time, close: day.close_time };
                    }
                    return acc;
                }, {});
            }

            // EXEMPT PROTECTION: Restore live map pins and photos
            if (venue.coordinates) updatedVenue.coordinates = venue.coordinates;
            if (venue.location) updatedVenue.location = venue.location;
            if (venue.photos) updatedVenue.photos = venue.photos;
        } else {
            // TIER 2: BLANK SLATE OVERWRITE (Ground Floor)
            updatedVenue.vibe = draft.vibe?.headline;
            updatedVenue.insiderVibe = draft.vibe?.insider_tip;
            updatedVenue.sceneTags = (draft.vibe?.audience_tags || []) as any[];
            updatedVenue.hasOutdoorSeating = draft.features?.has_outdoor_seating;

            updatedVenue.hours = draft.weekly_schedule?.reduce((acc: any, day) => {
                if (day.open_time && day.close_time) {
                    acc[day.day] = { open: day.open_time, close: day.close_time };
                }
                return acc;
            }, {}) || {};

            updatedVenue.carryingMakers = draft.inventory?.local_makers_featured?.length
                ? draft.inventory.local_makers_featured
                : [];

            // BLANK SLATE PROTECTION: Only restore photos (allow map overwrite)
            if (venue.photos) updatedVenue.photos = venue.photos;
        }

        // --- 5. SYSTEM CRITICAL LOCKS (Never Overwrite) ---
        const systemCriticalFields: (keyof Venue)[] = [
            'ownerId', 'managerIds', 'isPaidLeagueMember', 'tier_config', 'id'
        ];

        // Remove from draft payload and re-inject system truths
        systemCriticalFields.forEach(f => {
            delete (updatedVenue as any)[f];
            if ((venue as any)[f]) {
                (updatedVenue as any)[f] = (venue as any)[f];
            }
        });

        // 6. Game Features (Additive Merge - Always additive)
        const newGameFeatures = [...(venue.gameFeatures || [])];
        const featureMap = [
            { key: 'has_pool', type: 'pool_table', name: 'Pool Table' },
            { key: 'has_darts', type: 'darts', name: 'Dart Board' },
            { key: 'has_arcade_games', type: 'arcade_game', name: 'Arcade' },
            { key: 'has_karaoke', type: 'karaoke', name: 'Karaoke' },
            { key: 'has_trivia', type: 'trivia', name: 'Trivia' },
        ];

        featureMap.forEach(f => {
            if ((draft.features as any)[f.key] && !newGameFeatures.some(g => g.type === f.type)) {
                newGameFeatures.push({
                    id: `${venue.id}_${f.type}`,
                    type: f.type as any,
                    count: 1,
                    name: f.name,
                    status: 'active'
                });
            }
        });
        updatedVenue.gameFeatures = newGameFeatures;

        try {
            await updateVenueDetails(venue.id, updatedVenue);
            queryClient.invalidateQueries({ queryKey: ['venues-full'] });
            setExpandedVenueId(null);
        } catch (error) {
            console.error("Merge Failed:", error);
            alert("Protocol Violation: Could not merge document. Check logs.");
        } finally {
            setProcessingId(null);
        }
    };

    const handleReject = async (venueId: string) => {
        setProcessingId(venueId);
        try {
            await updateVenueDetails(venueId, {
                ai_draft_profile: null as any
            });
            queryClient.invalidateQueries({ queryKey: ['venues-full'] });
        } catch (error) {
            console.error("Reject Failed:", error);
        } finally {
            setProcessingId(null);
        }
    };

    if (drafts.length === 0) {
        return (
            <div className="flex flex-col items-center justify-center py-32 bg-slate-900/20 rounded-3xl border border-dashed border-white/5">
                <div className="w-16 h-16 bg-green-500/10 rounded-full flex items-center justify-center mb-6">
                    <Check className="w-8 h-8 text-green-500/40" />
                </div>
                <h3 className="text-xl font-black font-league uppercase text-slate-400">Refinery Idle</h3>
                <p className="text-[10px] text-slate-600 uppercase tracking-[0.2em] mt-2">All data is currently synchronized with the Artesian Canon.</p>
            </div>
        );
    }

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-end">
                <div>
                    <h2 className="text-2xl font-black font-league uppercase tracking-tight">Refinery Queue</h2>
                    <p className="text-[10px] font-black text-primary uppercase tracking-widest mt-1">Pending Human Verification</p>
                </div>
                <div className="bg-slate-900 px-3 py-1.5 rounded-lg border border-white/5 flex items-center gap-2">
                    <LayoutGrid size={14} className="text-slate-400" />
                    <span className="text-xs font-black font-mono">{drafts.length} DRAFTS</span>
                </div>
            </div>

            <div className="grid grid-cols-1 gap-4">
                {drafts.map(venue => (
                    <div
                        key={venue.id}
                        className={`group bg-slate-900/50 border transition-all duration-300 rounded-2xl overflow-hidden ${expandedVenueId === venue.id ? 'border-primary ring-1 ring-primary/20' : 'border-white/5 hover:border-white/10'
                            }`}
                    >
                        {/* Compact Header */}
                        <div
                            className="p-5 flex justify-between items-center cursor-pointer"
                            onClick={() => setExpandedVenueId(expandedVenueId === venue.id ? null : venue.id)}
                        >
                            <div className="flex items-center gap-4">
                                <div className="w-10 h-10 rounded-xl bg-black border border-white/10 flex items-center justify-center group-hover:bg-primary transition-colors">
                                    <Info size={18} className="text-slate-400 group-hover:text-black" />
                                </div>
                                <div>
                                    <h3 className="text-lg font-black text-white uppercase tracking-tight">{venue.name}</h3>
                                    <div className="flex items-center gap-3 mt-1 text-[10px] font-bold text-slate-500 uppercase">
                                        <span className="flex items-center gap-1">
                                            <AlertTriangle size={10} className="text-yellow-500" />
                                            {venue.ai_draft_profile?.metadata.confidence_score ? `${(venue.ai_draft_profile.metadata.confidence_score * 100).toFixed(0)}% Match` : 'New Data'}
                                        </span>
                                        <span className="w-1 h-1 rounded-full bg-slate-700" />
                                        <span>{venue.id}</span>
                                    </div>
                                </div>
                            </div>

                            <div className="flex items-center gap-3">
                                {expandedVenueId !== venue.id && (
                                    <>
                                        <button
                                            onClick={(e) => { e.stopPropagation(); handleReject(venue.id); }}
                                            disabled={processingId === venue.id}
                                            className="p-2 text-slate-500 hover:text-red-500 transition-colors"
                                        >
                                            <X size={20} />
                                        </button>
                                        <button
                                            onClick={(e) => { e.stopPropagation(); handleApprove(venue); }}
                                            disabled={processingId === venue.id}
                                            className="bg-primary text-black px-4 py-2 rounded-lg text-[10px] font-black uppercase tracking-widest hover:scale-105 active:scale-95 transition-all shadow-lg shadow-primary/10"
                                        >
                                            QUICK MERGE
                                        </button>
                                    </>
                                )}
                                <div className={`transition-transform duration-300 ${expandedVenueId === venue.id ? 'rotate-90 text-primary' : 'text-slate-600'}`}>
                                    <ChevronRight size={20} />
                                </div>
                            </div>
                        </div>

                        {/* Expanded Diff View */}
                        {expandedVenueId === venue.id && (
                            <div className="border-t border-white/5 bg-black/40 animate-in slide-in-from-top duration-300">
                                <div className="p-6 grid grid-cols-1 md:grid-cols-2 gap-8">

                                    {/* Column 1: Changes Summary */}
                                    <div className="space-y-6">
                                        <div>
                                            <h4 className="text-[10px] font-black text-slate-500 uppercase tracking-widest mb-4 border-b border-white/5 pb-2">Proposed Intelligence</h4>

                                            <div className="space-y-3">
                                                {/* Vibe Headline */}
                                                <div className="bg-white/5 p-3 rounded-xl border border-white/5">
                                                    <div className="text-[9px] font-bold text-primary uppercase mb-1">Headline</div>
                                                    <p className="text-xs text-white leading-relaxed italic">"{venue.ai_draft_profile?.vibe.headline}"</p>
                                                </div>

                                                {/* Features Diff */}
                                                <div className="grid grid-cols-2 gap-2">
                                                    {Object.entries(venue.ai_draft_profile?.features || {}).map(([key, val]) => (
                                                        <div key={key} className={`flex items-center justify-between p-2 rounded-lg border ${val ? 'bg-green-500/5 border-green-500/10' : 'bg-white/5 border-white/5 opacity-50'}`}>
                                                            <span className="text-[9px] font-bold uppercase text-slate-400">{key.replace('has_', '').replace('_', ' ')}</span>
                                                            {val ? <Check size={10} className="text-green-500" strokeWidth={4} /> : <X size={10} className="text-slate-600" />}
                                                        </div>
                                                    ))}
                                                </div>
                                            </div>
                                        </div>

                                        <div className="p-4 bg-primary/5 rounded-xl border border-primary/10">
                                            <div className="flex items-center gap-2 mb-2">
                                                <Info size={14} className="text-primary" />
                                                <span className="text-[10px] font-black text-primary uppercase">Surveyor Insight</span>
                                            </div>
                                            <p className="text-[10px] text-slate-400 leading-relaxed font-mono">
                                                {venue.ai_draft_profile?.metadata.verification_notes || "Data extracted via Google Places API (New) & Gemini Detective Protocol. Confidence in business hours is HIGH."}
                                            </p>
                                        </div>
                                    </div>

                                    {/* Column 2: Data Comparison Table */}
                                    <div>
                                        <h4 className="text-[10px] font-black text-slate-500 uppercase tracking-widest mb-4 border-b border-white/5 pb-2">Field Diff (Live vs Draft)</h4>
                                        <div className="space-y-2 font-mono text-[10px]">
                                            {[
                                                { label: 'Address', live: venue.address, draft: venue.address }, // Usually same
                                                { label: 'Website', live: venue.website, draft: venue.ai_draft_profile?.identity.website_url },
                                                { label: 'IG', live: venue.instagram, draft: venue.ai_draft_profile?.identity.social_links?.instagram },
                                                { label: 'Makers', live: venue.carryingMakers, draft: venue.ai_draft_profile?.inventory?.local_makers_featured?.join(', ') },
                                            ].map(field => (
                                                <div key={field.label} className="grid grid-cols-3 gap-2 py-1.5 border-b border-white/5 group/row">
                                                    <span className="text-slate-600 uppercase font-black">{field.label}</span>
                                                    <span className="text-slate-400 truncate opacity-50">{field.live || '---'}</span>
                                                    <span className={`truncate ${field.draft && field.draft !== field.live ? 'text-green-400 font-bold' : 'text-slate-500'}`}>
                                                        {field.draft || '---'}
                                                    </span>
                                                </div>
                                            ))}
                                        </div>

                                        <div className="mt-8 flex gap-3">
                                            <button
                                                onClick={() => handleReject(venue.id)}
                                                disabled={processingId === venue.id}
                                                className="flex-1 py-3 rounded-xl bg-red-500/10 text-red-500 text-[10px] font-black uppercase tracking-widest hover:bg-red-500/20 transition-all border border-red-500/20"
                                            >
                                                DISCARD DRAFT
                                            </button>
                                            <button
                                                onClick={() => handleApprove(venue)}
                                                disabled={processingId === venue.id}
                                                className="flex-[2] py-3 rounded-xl bg-primary text-black text-[10px] font-black uppercase tracking-widest hover:bg-yellow-400 transition-all shadow-xl shadow-primary/20 flex items-center justify-center gap-2"
                                            >
                                                {processingId === venue.id ? 'MERGING...' : <><Check size={14} strokeWidth={3} /> COMMIT TO CANON</>}
                                            </button>
                                        </div>
                                    </div>

                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

import React, { useState, useEffect } from 'react';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import {
    Shield, Users, BarChart3, Settings,
    Search, Filter, ExternalLink, Activity,
    Database, AlertTriangle, CheckCircle2, QrCode,
    Eye, EyeOff, Power, Archive, Gamepad2
} from 'lucide-react';

import { fetchAllUsers, fetchSystemStats, fetchRecentActivity } from '../../../services/userService';
import { fetchVenues, updateVenueDetails } from '../../../services/venueService';
import { UserProfile, ActivityLog, Venue } from '../../../types';
import { AiAccessTab } from '../components/AiAccessTab';
import { PhotoApprovalCard } from '../components/PhotoApprovalCard';
import { PinCalibrationMap } from '../components/PinCalibrationMap';
import { RefineryQueue } from '../components/RefineryQueue';
import { Camera, MapPin } from 'lucide-react';

interface AdminDashboardScreenProps {
    userProfile: any;
}

export const AdminDashboardScreen: React.FC<AdminDashboardScreenProps> = ({ userProfile }) => {
    const queryClient = useQueryClient();
    const [activeTab, setActiveTab] = useState<'overview' | 'users' | 'venues' | 'league' | 'system' | 'ai' | 'photos' | 'refinery'>('overview');
    const [systemStats, setSystemStats] = useState({ totalUsers: 0, activeUsers: 0, totalPoints: 0 });
    const [leagueUsers, setLeagueUsers] = useState<UserProfile[]>([]);
    const [recentActivity, setRecentActivity] = useState<ActivityLog[]>([]);

    const { data: venues = [] } = useQuery({
        queryKey: ['venues-full', activeTab],
        queryFn: () => fetchVenues(activeTab === 'refinery' ? false : true),
        enabled: activeTab === 'venues' || activeTab === 'overview' || activeTab === 'refinery',
    });
    const [searchTerm, setSearchTerm] = useState('');
    const [venueFilter, setVenueFilter] = useState<'all' | 'visible' | 'ghost' | 'archived'>('all');
    const [calibratingVenue, setCalibratingVenue] = useState<Venue | null>(null);

    useEffect(() => {
        const loadDashboard = async () => {
            const stats = await fetchSystemStats();
            setSystemStats(stats);
            const users = await fetchAllUsers();
            setLeagueUsers(users);
            const activity = await fetchRecentActivity();
            setRecentActivity(activity);
        };
        loadDashboard();
    }, []);

    const handleSaveCalibration = async (coords: { lat: number; lng: number }) => {
        if (!calibratingVenue) return;
        try {
            await updateVenueDetails(calibratingVenue.id, {
                location: { ...calibratingVenue.location, lat: coords.lat, lng: coords.lng }
            } as any);
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
            setCalibratingVenue(null);
        } catch (error) {
            console.error('Failed to save calibration', error);
        }
    };

    const filteredLeagueUsers = leagueUsers
        .filter(u => u.handle?.toLowerCase().includes(searchTerm.toLowerCase()) || u.uid.includes(searchTerm))
        .sort((a, b) => (b.stats?.seasonPoints || 0) - (a.stats?.seasonPoints || 0));

    const filteredVenues = venues
        .filter(v => v.name.toLowerCase().includes(searchTerm.toLowerCase()) || v.id.includes(searchTerm))
        .filter(v => {
            if (venueFilter === 'visible') return v.isVisible !== false && v.isActive !== false;
            if (venueFilter === 'ghost') return v.isVisible === false && v.isActive !== false;
            if (venueFilter === 'archived') return v.isActive === false;
            return true;
        })
        .sort((a, b) => a.name.localeCompare(b.name));

    const onToggleVenueMembership = async (venueId: string, currentStatus: boolean) => {
        // Optimistic Update
        queryClient.setQueryData(['venues-brief'], (old: Venue[] | undefined) => {
            return old?.map(v => v.id === venueId ? { ...v, isPaidLeagueMember: !currentStatus } : v);
        });

        try {
            await updateVenueDetails(venueId, { isPaidLeagueMember: !currentStatus });
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        } catch (error) {
            console.error('Failed to toggle membership', error);
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        }
    };

    const onToggleVisibility = async (venueId: string, currentStatus: boolean) => {
        // Optimistic Update
        queryClient.setQueryData(['venues-brief'], (old: Venue[] | undefined) => {
            return old?.map(v => v.id === venueId ? { ...v, isVisible: !currentStatus } : v);
        });

        try {
            await updateVenueDetails(venueId, { isVisible: !currentStatus });
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        } catch (error) {
            console.error('Failed to toggle visibility', error);
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        }
    };

    const onToggleActivity = async (venueId: string, currentStatus: boolean) => {
        // Optimistic Update
        queryClient.setQueryData(['venues-brief'], (old: Venue[] | undefined) => {
            return old?.map(v => v.id === venueId ? { ...v, isActive: !currentStatus } : v);
        });

        try {
            await updateVenueDetails(venueId, { isActive: !currentStatus });
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        } catch (error) {
            console.error('Failed to toggle activity', error);
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        }
    };

    const onToggleGameVibe = async (venueId: string, currentStatus: boolean) => {
        // Optimistic Update
        queryClient.setQueryData(['venues-brief'], (old: Venue[] | undefined) => {
            return old?.map(v => v.id === venueId ? { ...v, hasGameVibeCheckEnabled: !currentStatus } : v);
        });

        try {
            await updateVenueDetails(venueId, { hasGameVibeCheckEnabled: !currentStatus });
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        } catch (error) {
            console.error('Failed to toggle game vibe', error);
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        }
    };
    const handleApprovePhoto = async (venueId: string, photoId: string) => {
        const venue = venues.find(v => v.id === venueId);
        if (!venue || !venue.photos) return;

        const updatedPhotos = venue.photos.map(p =>
            p.id === photoId ? { ...p, marketingStatus: 'pending-venue', superAdminApprovedBy: userProfile.uid } as any : p
        );

        try {
            await updateVenueDetails(venueId, { photos: updatedPhotos });
            queryClient.invalidateQueries({ queryKey: ['venues-brief'] });
        } catch (error) {
            console.error('Failed to approve photo', error);
        }
    };

    const handleRejectPhoto = async (venueId: string, photoId: string) => {
        const venue = venues.find(v => v.id === venueId);
        if (!venue || !venue.photos) return;

        const updatedPhotos = venue.photos.map(p =>
            p.id === photoId ? { ...p, marketingStatus: 'rejected' } as any : p
        );

        try {
            await updateVenueDetails(venueId, { photos: updatedPhotos });
            queryClient.invalidateQueries({ queryKey: ['venues'] });
        } catch (error) {
            console.error('Failed to reject photo', error);
        }
    };

    const pendingPhotos = venues.flatMap(v =>
        (v.photos || [])
            .filter(p => p.marketingStatus === 'pending-super')
            .map(p => ({ venue: v, photo: p }))
    );



    const stats = [
        { label: 'Total Users', value: systemStats.totalUsers.toLocaleString(), icon: Users, color: 'text-blue-400' },
        { label: 'Active Users', value: systemStats.activeUsers.toString(), icon: Activity, color: 'text-primary' },
        { label: 'Total Points', value: systemStats.totalPoints.toLocaleString(), icon: Database, color: 'text-purple-400' },
        { label: 'System Health', value: '100%', icon: CheckCircle2, color: 'text-green-400' },
    ];

    return (
        <div className="min-h-screen bg-[#0f172a] text-white p-6 font-sans">
            {/* Header */}
            <div className="flex justify-between items-center mb-8 border-b border-white/5 pb-6">
                <div>
                    <div className="flex items-center gap-2 mb-1">
                        <Shield className="w-5 h-5 text-primary" />
                        <span className="text-[10px] font-black text-primary uppercase tracking-widest">Global Admin</span>
                    </div>
                    <h1 className="text-3xl font-black font-league uppercase tracking-tight">System Dashboard</h1>
                </div>
                <div className="text-right">
                    <p className="text-sm font-bold text-white uppercase">{userProfile.handle || 'Ryan Admin'}</p>
                    <p className="text-[10px] text-slate-400 font-mono">SUPER-ADMIN PRIVILEGES</p>
                </div>
            </div>

            {/* Quick Stats */}
            <div className="grid grid-cols-2 gap-4 mb-8">
                {stats.map((stat) => (
                    <div key={stat.label} className="bg-slate-900/50 border border-white/5 p-4 rounded-2xl shadow-xl">
                        <div className="flex justify-between items-start mb-2">
                            <stat.icon className={`w-5 h-5 ${stat.color}`} />
                        </div>
                        <p className="text-2xl font-black font-mono">{stat.value}</p>
                        <p className="text-[10px] text-slate-500 font-bold uppercase tracking-wider">{stat.label}</p>
                    </div>
                ))}
            </div>

            {/* Tabs */}
            <div className="flex gap-2 mb-6 bg-black/40 p-1 rounded-xl overflow-x-auto">
                {(['overview', 'users', 'venues', 'photos', 'refinery', 'league', 'system', 'ai'] as const).map((tab) => (
                    <button
                        key={tab}
                        onClick={() => setActiveTab(tab)}
                        className={`flex-1 py-2 px-4 text-[10px] font-black uppercase tracking-widest rounded-lg transition-all whitespace-nowrap ${activeTab === tab
                            ? 'bg-primary text-black shadow-lg'
                            : 'text-slate-500 hover:text-white'
                            }`}
                    >
                        {tab === 'ai' ? 'AI Access' : tab}
                    </button>
                ))}
            </div>

            {/* Main Content Area */}
            <div className="bg-slate-900 border border-white/5 rounded-2xl p-6 min-h-[300px] shadow-2xl">
                {activeTab === 'overview' && (
                    <div className="space-y-6">
                        <div className="flex justify-between items-center">
                            <h2 className="text-lg font-black font-league uppercase">Live Activity Loop</h2>
                            <span className="flex items-center gap-1.5 px-2 py-0.5 rounded-full bg-green-500/10 text-green-400 text-[10px] font-bold">
                                <div className="w-1.5 h-1.5 rounded-full bg-green-400 animate-pulse" />
                                Live
                            </span>
                        </div>

                        <div className="space-y-2">
                            {recentActivity.length === 0 ? (
                                <div className="text-center py-10 opacity-50">
                                    <p className="text-xs uppercase tracking-widest">No recent activity found.</p>
                                </div>
                            ) : (
                                recentActivity.map((log) => (
                                    <div key={log.id} className="flex justify-between items-center bg-black/20 p-3 rounded-lg border border-white/5">
                                        <div className="flex items-center gap-3">
                                            <div className={`w-8 h-8 rounded-full flex items-center justify-center ${log.verificationMethod === 'qr' ? 'bg-yellow-500/20 text-yellow-500' : 'bg-slate-700/50 text-slate-400'}`}>
                                                {log.verificationMethod === 'qr' ? <QrCode className="w-4 h-4" /> : <Activity className="w-4 h-4" />}
                                            </div>
                                            <div>
                                                <p className="text-xs font-bold text-white uppercase">{log.type.replace('_', ' ')}</p>
                                                <p className="text-[10px] text-slate-500 font-mono">
                                                    User: {log.userId.substring(0, 6)}... {log.venueId ? `@ ${log.venueId}` : ''}
                                                </p>
                                            </div>
                                        </div>
                                        <div className="text-right">
                                            <div className="flex items-center justify-end gap-1">
                                                {log.verificationMethod === 'qr' && (
                                                    <span className="text-[9px] font-black bg-yellow-500 text-black px-1.5 rounded uppercase">Verified</span>
                                                )}
                                                <span className="text-primary font-black font-mono text-xs">+{log.points}</span>
                                            </div>
                                            <p className="text-[9px] text-slate-600">{new Date(log.timestamp).toLocaleTimeString()}</p>
                                        </div>
                                    </div>
                                ))
                            )}
                        </div>
                    </div>
                )}

                {activeTab === 'venues' && (
                    <div className="space-y-4">
                        <div className="flex justify-between items-center bg-black/20 p-3 rounded-xl border border-white/5">
                            <h2 className="text-lg font-black font-league uppercase">Venue Roster</h2>
                            <div className="flex gap-2 mr-4">
                                {(['all', 'visible', 'ghost', 'archived'] as const).map(f => (
                                    <button
                                        key={f}
                                        onClick={() => setVenueFilter(f)}
                                        className={`px-3 py-1 rounded-lg text-[10px] font-black uppercase tracking-widest transition-all ${venueFilter === f ? 'bg-primary text-black' : 'text-slate-500 hover:text-white bg-black/40'
                                            }`}
                                    >
                                        {f}
                                    </button>
                                ))}
                            </div>
                            <div className="relative">
                                <Search className="w-4 h-4 text-slate-500 absolute left-3 top-1/2 -translate-y-1/2" />
                                <input
                                    type="text"
                                    placeholder="Find Venue..."
                                    className="bg-slate-800 border-none rounded-lg py-2 pl-9 pr-4 text-xs font-bold text-white focus:ring-1 focus:ring-primary outline-none"
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                            </div>
                        </div>

                        <div className="overflow-x-auto">
                            <table className="w-full text-left border-collapse">
                                <thead>
                                    <tr className="text-[9px] text-slate-500 uppercase tracking-widest border-b border-white/10">
                                        <th className="p-3">Venue Name</th>
                                        <th className="p-3">ID</th>
                                        <th className="p-3 text-center">Paid Member</th>
                                        <th className="p-3 text-center">Game Vibe</th>
                                        <th className="p-3 text-center">Ghost Mode</th>
                                        <th className="p-3 text-center">Map</th>
                                        <th className="p-3 text-right">Status</th>
                                    </tr>
                                </thead>
                                <tbody className="divide-y divide-white/5">
                                    {filteredVenues.map((venue) => (
                                        <tr key={venue.id} className="hover:bg-white/5 transition-colors">
                                            <td className="p-3">
                                                <div className="font-bold text-white">{venue.name}</div>
                                                <div className="text-[9px] text-slate-500 uppercase">{venue.vibe}</div>
                                            </td>
                                            <td className="p-3 font-mono text-[10px] text-slate-500">{venue.id}</td>
                                            <td className="p-3 text-center">
                                                <button
                                                    onClick={() => onToggleVenueMembership(venue.id, !!venue.isPaidLeagueMember)}
                                                    className={`px-3 py-1 rounded-full text-[10px] font-black uppercase tracking-widest transition-all ${venue.isPaidLeagueMember
                                                        ? 'bg-primary text-black shadow-[0_0_10px_rgba(251,191,36,0.5)]'
                                                        : 'bg-slate-800 text-slate-500 hover:bg-slate-700'
                                                        }`}
                                                >
                                                    {venue.isPaidLeagueMember ? 'Active' : 'Unpaid'}
                                                </button>
                                            </td>
                                            <td className="p-3 text-center">
                                                <button
                                                    onClick={() => onToggleVisibility(venue.id, venue.isVisible !== false)}
                                                    className={`p-2 rounded-lg transition-all ${venue.isVisible !== false
                                                        ? 'text-slate-600 hover:text-white'
                                                        : 'bg-red-500/10 text-red-500 hover:bg-red-500/20'
                                                        }`}
                                                    title={venue.isVisible !== false ? "Visible Publicly" : "Ghost Mode (Hidden)"}
                                                >
                                                    {venue.isVisible !== false ? <Eye size={16} /> : <EyeOff size={16} />}
                                                </button>
                                            </td>
                                            <td className="p-3 text-center">
                                                <button
                                                    onClick={() => setCalibratingVenue(venue)}
                                                    className="p-2 hover:bg-primary/20 text-slate-400 hover:text-primary transition-all rounded-lg"
                                                    title="Calibrate Map Pin"
                                                >
                                                    <MapPin size={16} />
                                                </button>
                                            </td>
                                            <td className="p-3 text-right">
                                                <button
                                                    onClick={() => onToggleActivity(venue.id, venue.isActive !== false)}
                                                    className={`p-2 rounded-lg transition-all ${venue.isActive !== false
                                                        ? 'text-green-500 hover:text-green-400'
                                                        : 'text-slate-600 hover:text-white'
                                                        }`}
                                                    title={venue.isActive !== false ? "Active" : "Archived"}
                                                >
                                                    {venue.isActive !== false ? <Power size={16} /> : <Archive size={16} />}
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                )}

                {activeTab === 'users' && (
                    <div className="space-y-4">
                        <div className="flex justify-between items-center bg-black/20 p-3 rounded-xl border border-white/5">
                            <h2 className="text-lg font-black font-league uppercase">User Directory</h2>
                            <div className="relative">
                                <Search className="w-4 h-4 text-slate-500 absolute left-3 top-1/2 -translate-y-1/2" />
                                <input
                                    type="text"
                                    placeholder="Search by name, email, or UID..."
                                    className="bg-slate-800 border-none rounded-lg py-2 pl-9 pr-4 text-xs font-bold text-white focus:ring-1 focus:ring-primary outline-none min-w-[300px]"
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                            </div>
                        </div>

                        <div className="overflow-x-auto">
                            <table className="w-full text-left border-collapse">
                                <thead>
                                    <tr className="text-[9px] text-slate-500 uppercase tracking-widest border-b border-white/10">
                                        <th className="p-3">User</th>
                                        <th className="p-3">UID</th>
                                        <th className="p-3 text-right">Role</th>
                                        <th className="p-3 text-right">Points</th>
                                        <th className="p-3 text-center">Status</th>
                                    </tr>
                                </thead>
                                <tbody className="divide-y divide-white/5">
                                    {leagueUsers
                                        .filter(u =>
                                            u.handle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                                            u.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                                            u.uid.includes(searchTerm)
                                        )
                                        .map((user) => (
                                            <tr key={user.uid} className="hover:bg-white/5 transition-colors">
                                                <td className="p-3">
                                                    <div className="font-bold text-white">{user.handle || 'Guest'}</div>
                                                    <div className="text-[10px] text-slate-500 lowercase">{user.email || 'No Email'}</div>
                                                </td>
                                                <td className="p-3 font-mono text-[9px] text-slate-600">{user.uid}</td>
                                                <td className="p-3 text-right uppercase font-bold text-[10px]">
                                                    <span className={`px-2 py-0.5 rounded-full ${user.role === 'super-admin'
                                                        ? 'bg-red-500/10 text-red-500 border border-red-500/20'
                                                        : user.role === 'owner'
                                                            ? 'bg-primary/10 text-primary border border-primary/20'
                                                            : 'bg-blue-500/10 text-blue-400 border border-blue-500/20'
                                                        }`}>
                                                        {user.role}
                                                    </span>
                                                </td>
                                                <td className="p-3 text-right font-mono text-primary font-black">
                                                    {(user.stats?.seasonPoints || 0).toLocaleString()}
                                                </td>
                                                <td className="p-3 text-center">
                                                    <div className={`w-2 h-2 rounded-full mx-auto ${user.email ? 'bg-green-500' : 'bg-slate-700'}`} />
                                                </td>
                                            </tr>
                                        ))}
                                </tbody>
                            </table>
                            {leagueUsers.length === 0 && (
                                <div className="text-center py-20 opacity-50">
                                    <Users className="w-12 h-12 mb-4 mx-auto text-slate-600" />
                                    <p className="text-sm font-bold uppercase tracking-widest">No users found</p>
                                </div>
                            )}
                        </div>
                    </div>
                )}

                {activeTab === 'league' && (
                    <div className="space-y-4">
                        <div className="flex justify-between items-center bg-black/20 p-3 rounded-xl border border-white/5">
                            <h2 className="text-lg font-black font-league uppercase">League Roster</h2>
                            <div className="relative">
                                <Search className="w-4 h-4 text-slate-500 absolute left-3 top-1/2 -translate-y-1/2" />
                                <input
                                    type="text"
                                    placeholder="Find Member..."
                                    className="bg-slate-800 border-none rounded-lg py-2 pl-9 pr-4 text-xs font-bold text-white focus:ring-1 focus:ring-primary outline-none"
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                            </div>
                        </div>

                        <div className="overflow-x-auto">
                            <table className="w-full text-left border-collapse">
                                <thead>
                                    <tr className="text-[9px] text-slate-500 uppercase tracking-widest border-b border-white/10">
                                        <th className="p-3">Rank</th>
                                        <th className="p-3">Handle</th>
                                        <th className="p-3 text-right">Points</th>
                                        <th className="p-3 text-right">Role</th>
                                        <th className="p-3 text-center">Status</th>
                                    </tr>
                                </thead>
                                <tbody className="divide-y divide-white/5">
                                    {filteredLeagueUsers.map((user, idx) => (
                                        <tr key={user.uid} className="hover:bg-white/5 transition-colors">
                                            <td className="p-3 font-mono font-bold text-slate-400">#{idx + 1}</td>
                                            <td className="p-3 font-bold text-white">{user.handle || 'Unknown'}</td>
                                            <td className="p-3 text-right font-mono text-primary font-black">
                                                {(user.stats?.seasonPoints || 0).toLocaleString()}
                                            </td>
                                            <td className="p-3 text-right text-[10px] uppercase font-bold text-slate-500">
                                                {user.role}
                                            </td>
                                            <td className="p-3 text-center">
                                                <div className={`w-2 h-2 rounded-full mx-auto ${user.role !== 'guest' ? 'bg-green-500' : 'bg-slate-700'}`} />
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                            {filteredLeagueUsers.length === 0 && (
                                <div className="text-center py-8 text-slate-500 text-xs uppercase">No members found</div>
                            )}
                        </div>
                    </div>
                )}

                {activeTab === 'system' && (
                    <div className="space-y-4">
                        <h2 className="text-lg font-black font-league uppercase">Infrastructure State</h2>
                        <div className="grid grid-cols-1 gap-2">
                            <div className="p-3 bg-black/20 border border-white/5 rounded-xl flex justify-between items-center">
                                <span className="text-[10px] font-bold uppercase">Cloud Run</span>
                                <span className="text-[10px] font-mono text-green-400">OPERATIONAL</span>
                            </div>
                            <div className="p-3 bg-black/20 border border-white/5 rounded-xl flex justify-between items-center">
                                <span className="text-[10px] font-bold uppercase">Firestore</span>
                                <span className="text-[10px] font-mono text-green-400">OPERATIONAL</span>
                            </div>
                            {/* <div className="p-3 bg-black/20 border border-white/5 rounded-xl flex justify-between items-center">
                                <span className="text-[10px] font-bold uppercase">Gemini SDK</span>
                                <span className="text-[10px] font-mono text-green-400">READY</span>
                             </div> */}
                        </div>
                    </div>
                )}

                {activeTab === 'photos' && (
                    <div className="space-y-6">
                        <div className="flex justify-between items-center">
                            <h2 className="text-lg font-black font-league uppercase">Photo Quality Control</h2>
                            <span className="bg-primary/20 text-primary px-3 py-1 rounded-full text-[10px] font-black uppercase tracking-widest">
                                {pendingPhotos.length} Pending Approval
                            </span>
                        </div>

                        {pendingPhotos.length === 0 ? (
                            <div className="flex flex-col items-center justify-center py-20 bg-black/20 rounded-2xl border border-dashed border-white/5">
                                <Camera className="w-12 h-12 text-slate-700 mb-4" />
                                <p className="text-sm font-bold text-slate-500 uppercase">All caught up!</p>
                                <p className="text-[10px] text-slate-600 uppercase mt-1">No new photos need verification.</p>
                            </div>
                        ) : (
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                                {pendingPhotos.map(({ venue, photo }) => (
                                    <PhotoApprovalCard
                                        key={photo.id}
                                        venue={venue}
                                        photo={photo}
                                        onApprove={handleApprovePhoto}
                                        onReject={handleRejectPhoto}
                                        isAdminView={true}
                                    />
                                ))}
                            </div>
                        )}
                    </div>
                )}

                {activeTab === 'refinery' && <RefineryQueue venues={venues} />}

                {activeTab === 'ai' && <AiAccessTab />}
            </div>

            {/* Calibration Modal */}
            {calibratingVenue && (
                <div className="fixed inset-0 z-[100] flex items-center justify-center p-6 bg-black/80 backdrop-blur-sm animate-in fade-in duration-300">
                    <div className="w-full max-w-2xl">
                        <PinCalibrationMap
                            venue={calibratingVenue}
                            onSave={handleSaveCalibration}
                            onCancel={() => setCalibratingVenue(null)}
                        />
                    </div>
                </div>
            )}

            <div className="mt-8 p-4 bg-amber-400/10 border border-amber-400/20 rounded-2xl flex gap-3">
                <AlertTriangle className="w-5 h-5 text-amber-400 shrink-0" />
                <div>
                    <p className="text-[10px] font-black text-amber-400 uppercase tracking-widest mb-1">Audit Warning</p>
                    <p className="text-[11px] text-slate-300 font-medium">All admin actions are logged to Secret Manager and Cloud Audit. Proceed with League Integrity in mind.</p>
                </div>
            </div>
        </div>
    );
};

import React, { useState } from 'react';
import {
  X, User, Hash, Home, Beer, Mail, Phone, ChevronRight, Shield, Lock, Facebook
} from 'lucide-react';
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  getMultiFactorResolver,
  PhoneAuthProvider,
  PhoneMultiFactorGenerator,
  RecaptchaVerifier,
  multiFactor,
  User as FirebaseUser
} from 'firebase/auth';
import { doc, setDoc, getDoc } from 'firebase/firestore';
import { auth, db } from '../../../lib/firebase';
import { UserProfile, Venue, UserRole, SystemRole } from '../../../types';
import { useToast } from '../../../components/ui/BrandedToast';
import { mapAuthErrorToMessage } from '../utils/authErrorHandler';
import { AuthService } from '../../../services/authService';

interface LoginModalProps {
  isOpen: boolean;
  onClose: () => void;
  loginMode: 'user' | 'owner';
  setLoginMode: (mode: 'user' | 'owner') => void;
  userSubMode: 'login' | 'signup';
  setUserSubMode: (mode: 'login' | 'signup') => void;
  userProfile: UserProfile;
  setUserProfile: React.Dispatch<React.SetStateAction<UserProfile>>;
  venues: Venue[];
  alertPrefs: any;
  setAlertPrefs: (prefs: any) => void;
  openInfo: (title: string, text: string) => void;
  onOwnerSuccess: () => void;
}

export const LoginModal: React.FC<LoginModalProps> = ({
  isOpen,
  onClose,
  loginMode,
  setLoginMode,
  userSubMode,
  setUserSubMode,
  userProfile,
  setUserProfile,
  venues,
  alertPrefs,
  setAlertPrefs,
  openInfo,
  onOwnerSuccess
}) => {
  const { showToast } = useToast();
  const [email, setEmail] = useState(userProfile.email || '');
  const [password, setPassword] = useState('');
  const [handle, setHandle] = useState(userProfile.handle || '');
  const [phone, setPhone] = useState(userProfile.phone || '');
  const [drink, setDrink] = useState(userProfile.favoriteDrink || '');
  const [homeBase, setHomeBase] = useState(userProfile.homeBase || '');

  const [ownerEmail, setOwnerEmail] = useState('');
  const [ownerPassword, setOwnerPassword] = useState('');

  // Onboarding specific state
  const [joinLeague, setJoinLeague] = useState(true);
  const [selectedInterests, setSelectedInterests] = useState<string[]>([]);
  const [weeklyBuzz, setWeeklyBuzz] = useState(true);

  const [isLoading, setIsLoading] = useState(false);
  const [acceptTerms, setAcceptTerms] = useState(false);

  // MFA State
  const [mfaResolver, setMfaResolver] = useState<any>(null);
  const [mfaId, setMfaId] = useState('');
  const [mfaCode, setMfaCode] = useState('');
  const [showMfaStep, setShowMfaStep] = useState(false);
  const [recaptchaVerifier, setRecaptchaVerifier] = useState<any>(null);

  const interestOptions = [
    { id: 'karaoke', label: 'Karaoke', icon: '🎤' },
    { id: 'trivia', label: 'Trivia', icon: '🧠' },
    { id: 'live_music', label: 'Live Music', icon: '🎸' },
    { id: 'arcade', label: 'Arcade', icon: '👾' },
  ];

  if (!isOpen) return null;

  const handleUserLogin = async () => {
    if (!email.includes('@')) { showToast('Please enter a valid email.'); return; }
    if (!password) { showToast('Please enter your password.'); return; }

    setIsLoading(true);
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const uid = userCredential.user.uid;
      const profileSnap = await getDoc(doc(db, 'users', uid));

      if (profileSnap.exists()) {
        const profileData = profileSnap.data() as UserProfile;
        setUserProfile(profileData);
        onClose();
        showToast(`Welcome back, ${profileData.handle || 'Legend'}!`, 'success');
      } else {
        showToast('Profile not found. Please register.');
        setUserSubMode('signup');
      }
    } catch (error: any) {
      showToast(mapAuthErrorToMessage(error.code));
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleLogin = async () => {
    setIsLoading(true);
    try {
      const profile = await AuthService.signInWithGoogle();

      if (loginMode === 'owner') {
        // RBAC Access Check for Venue Login
        const hasAccess =
          profile.systemRole === 'admin' ||
          (profile.venuePermissions && Object.keys(profile.venuePermissions).length > 0) ||
          ['admin', 'owner', 'manager', 'super-admin'].includes(profile.role);

        if (!hasAccess) {
          showToast(`Access Denied: ${profile.email} is not authorized for Venue management.`);
          setIsLoading(false);
          return;
        }

        // Check for MFA Enrollment for Owners/Managers
        const firebaseUser = auth.currentUser;
        if (firebaseUser && multiFactor(firebaseUser).enrolledFactors.length === 0) {
          showToast('MFA REQUIRED FOR PARTNERS. PLEASE ENROLL IN SETTINGS.', 'info');
          // We might want to force enrollment here, but for now we'll just warn and let App.tsx block access if needed.
        }
      }

      setUserProfile(profile);
      const welcomeName = profile.handle || profile.displayName || 'Legend';
      showToast(loginMode === 'owner' ? `Logged in as Commissioner ${welcomeName}` : `Welcome to the League, ${welcomeName}!`, 'success');

      if (loginMode === 'owner') {
        onOwnerSuccess();
      }
      onClose();
    } catch (error: any) {
      if (error.code === 'auth/multi-factor-auth-required') {
        await handleMfaRequired(error);
      } else if (error.code !== 'auth/popup-closed-by-user') {
        showToast(mapAuthErrorToMessage(error.code));
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleFacebookLogin = async () => {
    setIsLoading(true);
    try {
      const profile = await AuthService.signInWithFacebook();

      if (loginMode === 'owner') {
        const hasAccess =
          profile.systemRole === 'admin' ||
          (profile.venuePermissions && Object.keys(profile.venuePermissions).length > 0) ||
          ['admin', 'owner', 'manager', 'super-admin'].includes(profile.role);

        if (!hasAccess) {
          showToast(`Access Denied: ${profile.email} is not authorized for Venue management.`);
          setIsLoading(false);
          return;
        }

        const firebaseUser = auth.currentUser;
        if (firebaseUser && multiFactor(firebaseUser).enrolledFactors.length === 0) {
          showToast('MFA REQUIRED FOR PARTNERS. PLEASE ENROLL IN SETTINGS.', 'info');
        }
      }

      setUserProfile(profile);
      const welcomeName = profile.handle || profile.displayName || 'Legend';
      showToast(loginMode === 'owner' ? `Logged in as Commissioner ${welcomeName}` : `Welcome to the League, ${welcomeName}!`, 'success');

      if (loginMode === 'owner') {
        onOwnerSuccess();
      }
      onClose();
    } catch (error: any) {
      if (error.code === 'auth/multi-factor-auth-required') {
        await handleMfaRequired(error);
      } else if (error.code !== 'auth/popup-closed-by-user') {
        showToast(mapAuthErrorToMessage(error.code));
      }
    } finally {
      setIsLoading(false);
    }
  };

  const saveUser = async () => {
    if (!handle.trim()) { showToast('You need a Handle for the League!'); return; }
    if (!email.includes('@')) { showToast('Please enter a valid email.'); return; }
    if (password.length < 6) { showToast('Password must be at least 6 characters.'); return; }
    if (!acceptTerms) { showToast('Please accept the Terms & Conditions.'); return; }

    setIsLoading(true);
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const uid = userCredential.user.uid;

      const newProfile: UserProfile = {
        uid: uid,
        role: joinLeague ? 'user' : 'guest', // Legacy support
        systemRole: 'guest', // RBAC Default
        venuePermissions: {}, // RBAC Default
        handle,
        email,
        phone,
        homeBase,
        favoriteDrinks: drink ? [drink] : [],
        weeklyBuzz: weeklyBuzz,
        showMemberSince: true,
        createdAt: Date.now(),
        stats: joinLeague ? {
          seasonPoints: 50,
          lifetimeClockins: 0,
          currentStreak: 0,
          vibeCheckCount: 0,
          competitionPoints: 0
        } : undefined
      };

      setAlertPrefs({
        ...alertPrefs,
        weeklyDigest: weeklyBuzz,
        interests: selectedInterests
      });

      await setDoc(doc(db, 'users', uid), newProfile);
      setUserProfile(newProfile);
      onClose();
      showToast(joinLeague ? `Welcome to the League, ${handle}!` : `Welcome to OlyBars, ${handle}!`, 'success');
    } catch (error: any) {
      console.error("Registration Error:", error);
      showToast(mapAuthErrorToMessage(error.code));
    }
  };

  const handleOwnerLogin = async () => {
    if (!ownerEmail.includes('@')) { showToast('Please enter a valid email.'); return; }
    setIsLoading(true);
    try {
      const userCredential = await signInWithEmailAndPassword(auth, ownerEmail, ownerPassword);
      await finishLogin(userCredential.user);
    } catch (error: any) {
      if (error.code === 'auth/multi-factor-auth-required') {
        await handleMfaRequired(error);
      } else {
        showToast(mapAuthErrorToMessage(error.code));
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleMfaRequired = async (error: any) => {
    const resolver = getMultiFactorResolver(auth, error);
    setMfaResolver(resolver);

    // Auto-trigger SMS to the first enrolled factor
    const hints = resolver.hints;
    if (hints[0] && hints[0].factorId === 'phone') {
      const verifier = new RecaptchaVerifier(auth, 'recaptcha-container', {
        'size': 'invisible'
      });
      setRecaptchaVerifier(verifier);
      const phoneInfoOptions = {
        multiFactorHint: hints[0],
        session: resolver.session
      };
      const phoneAuthProvider = new PhoneAuthProvider(auth);
      const verificationId = await phoneAuthProvider.verifyPhoneNumber(phoneInfoOptions, verifier);
      setMfaId(verificationId);
      setShowMfaStep(true);
      showToast('ONE-TIME CODE SENT TO SECURE DEVICE', 'success');
    } else {
      showToast('MFA REQUIRED: PLEASE CONTACT HQ FOR SETUP', 'error');
    }
  };

  const handleVerifyMfa = async () => {
    if (!mfaCode || !mfaResolver || !mfaId) return;
    setIsLoading(true);
    try {
      const cred = PhoneAuthProvider.credential(mfaId, mfaCode);
      const multiFactorAssertion = PhoneMultiFactorGenerator.assertion(cred);
      const userCredential = await mfaResolver.resolveSignIn(multiFactorAssertion);
      await finishLogin(userCredential.user);
    } catch (error: any) {
      showToast('INVALID MFA CODE. PLEASE RETRY.', 'error');
    } finally {
      setIsLoading(false);
    }
  };

  const finishLogin = async (firebaseUser: FirebaseUser) => {
    const uid = firebaseUser.uid;
    const profileSnap = await getDoc(doc(db, 'users', uid));
    if (profileSnap.exists()) {
      const profileData = profileSnap.data() as UserProfile;

      if (firebaseUser.email === 'ryan@amaspc.com') {
        // The Ryan Rule: Always force super-admin rights
        // Adding venuePermissions: {} to trigger the check correctly in all screens
        const superAdminData = {
          role: 'super-admin' as UserRole, // Legacy
          systemRole: 'admin' as SystemRole, // RBAC Master Key
          handle: 'Ryan (Admin)',
          email: 'ryan@amaspc.com',
          venuePermissions: profileData.venuePermissions || {}, // Keep existing if any, but systemRole: admin overrides anyway
        };

        await setDoc(doc(db, 'users', uid), {
          ...superAdminData,
          // Fix: Reset 9999 points if present
          ...(profileData.stats?.seasonPoints === 9999 ? { stats: { ...profileData.stats, seasonPoints: 0 } } : {})
        }, { merge: true });

        setUserProfile({ ...profileData, ...superAdminData });
        onOwnerSuccess();
        onClose();
        showToast(`Logged in as SUPER-ADMIN (Golden Ticket)`, 'success');
        return;
      }

      setUserProfile(profileData);

      // RBAC Access Check
      const hasAccess =
        profileData.systemRole === 'admin' ||
        (profileData.venuePermissions && Object.keys(profileData.venuePermissions).length > 0) ||
        ['admin', 'owner', 'manager', 'super-admin'].includes(profileData.role);

      if (hasAccess) {
        // Check for enrollment for partners
        if (multiFactor(firebaseUser).enrolledFactors.length === 0) {
          showToast('MFA ENROLLMENT REQUIRED FOR PARTNER ACCESS', 'info');
        }
        onOwnerSuccess();
        onClose();
        showToast(`Logged in as ${profileData.handle || 'Owner'}`, 'success');
      } else {
        showToast(`Access Denied: Venue account required.`);
        onClose();
      }
    } else {
      showToast('Profile not found.');
    }
  };

  const inputClasses = "w-full bg-slate-100 border border-slate-300 focus:border-primary rounded-md py-2 text-sm text-black outline-none font-bold pl-10";

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-[70] flex items-center justify-center p-4">
      <div className="bg-surface w-full max-w-sm border-2 border-slate-700 shadow-lg rounded-xl relative flex flex-col max-h-[90vh] overflow-hidden">
        {userSubMode === 'login' && !showMfaStep && (
          <div className="flex border-b-2 border-slate-700">
            <button onClick={() => setLoginMode('user')} className={`flex-1 py-3 font-bold uppercase ${loginMode === 'user' ? 'bg-primary text-black' : 'text-slate-400'}`}>Player</button>
            <button onClick={() => setLoginMode('owner')} className={`flex-1 py-3 font-bold uppercase ${loginMode === 'owner' ? 'bg-primary text-black' : 'text-slate-400'}`}>Partner</button>
          </div>
        )}

        <div className="p-6 overflow-y-auto text-white">
          <button onClick={onClose} className="absolute top-3 right-3 text-slate-500"><X className="w-5 h-5" /></button>

          <div id="recaptcha-container"></div>

          {showMfaStep ? (
            <div className="space-y-6 text-center">
              <div className="bg-primary/20 w-16 h-16 rounded-full flex items-center justify-center mx-auto border border-primary/30">
                <Shield className="w-8 h-8 text-primary" />
              </div>
              <div>
                <h3 className="text-xl font-bold uppercase">Identity Verification</h3>
                <p className="text-[10px] text-slate-400 uppercase tracking-widest mt-1">Check your text messages for a code</p>
              </div>

              <div className="relative">
                <Hash className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                <input
                  type="text"
                  value={mfaCode}
                  onChange={(e) => setMfaCode(e.target.value)}
                  placeholder="6-Digit Code"
                  className={inputClasses}
                  maxLength={6}
                />
              </div>

              <button
                onClick={handleVerifyMfa}
                disabled={isLoading || mfaCode.length < 6}
                className="w-full bg-primary text-black font-bold py-3 rounded uppercase disabled:opacity-50"
              >
                {isLoading ? 'Verifying...' : 'Unlock Dashboard'}
              </button>

              <button
                onClick={() => setShowMfaStep(false)}
                className="text-[10px] text-slate-500 hover:text-white uppercase font-bold tracking-widest"
              >
                Cancel
              </button>
            </div>
          ) : loginMode === 'user' ? (
            <>
              <form onSubmit={(e) => { e.preventDefault(); userSubMode === 'signup' ? saveUser() : handleUserLogin(); }} className="space-y-4">
                <div className="text-center">
                  <h3 className="text-xl font-bold uppercase">{userSubMode === 'signup' ? 'Create Account' : 'Player Login'}</h3>
                  <p className="text-[10px] text-slate-400 uppercase">{userSubMode === 'signup' ? 'Save favorites & earn points' : 'Level up your night'}</p>
                </div>

                {userSubMode === 'signup' && (
                  <div className="relative">
                    <Hash className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                    <input type="text" value={handle} onChange={(e) => setHandle(e.target.value)} placeholder="Handle" className={inputClasses} />
                  </div>
                )}

                <div className="relative">
                  <Mail className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                  <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className={inputClasses} />
                </div>

                <div className="relative">
                  <Lock className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                  <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className={inputClasses} autoComplete="current-password" />
                </div>

                {userSubMode === 'signup' && (
                  <>
                    <div className="relative">
                      <Phone className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                      <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} placeholder="Phone (Optional)" className={inputClasses} />
                    </div>

                    <div className="relative">
                      <Beer className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                      <input type="text" value={drink} onChange={(e) => setDrink(e.target.value)} placeholder="Preferred Sips (e.g. IPA)" className={inputClasses} />
                    </div>

                    <div className="relative">
                      <Home className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                      <select
                        value={homeBase}
                        onChange={(e) => setHomeBase(e.target.value)}
                        className={`${inputClasses} appearance-none cursor-pointer`}
                      >
                        <option value="" disabled className="text-black">Select Home Base</option>
                        {venues.map(v => (
                          <option key={v.id} value={v.id} className="text-black">{v.name}</option>
                        ))}
                      </select>
                    </div>
                  </>
                )}

                {userSubMode === 'signup' && (
                  <div className="pt-4 border-t border-slate-700 space-y-4">
                    <div className="flex items-center justify-between">
                      <span className="text-xs font-bold uppercase">Join League?</span>
                      <button type="button" onClick={() => setJoinLeague(!joinLeague)} className={`w-10 h-5 rounded-full p-1 ${joinLeague ? 'bg-primary' : 'bg-slate-700'}`}>
                        <div className={`w-3 h-3 bg-white rounded-full transition-transform ${joinLeague ? 'translate-x-5' : 'translate-x-0'}`} />
                      </button>
                    </div>

                    {joinLeague && (
                      <div className="grid grid-cols-2 gap-2">
                        {interestOptions.map(opt => (
                          <button key={opt.id} type="button" onClick={() => setSelectedInterests(prev => prev.includes(opt.id) ? prev.filter(i => i !== opt.id) : [...prev, opt.id])}
                            className={`p-2 border rounded text-[10px] font-bold uppercase ${selectedInterests.includes(opt.id) ? 'bg-primary text-black' : 'border-slate-700 text-slate-400'}`}>
                            {opt.label}
                          </button>
                        ))}
                      </div>
                    )}

                    <div className="flex items-center justify-between">
                      <span className="text-xs font-bold uppercase">Weekly Digest?</span>
                      <button type="button" onClick={() => setWeeklyBuzz(!weeklyBuzz)} className={`w-10 h-5 rounded-full p-1 ${weeklyBuzz ? 'bg-primary' : 'bg-slate-700'}`}>
                        <div className={`w-3 h-3 bg-white rounded-full transition-transform ${weeklyBuzz ? 'translate-x-5' : 'translate-x-0'}`} />
                      </button>
                    </div>

                    <div className="flex items-center gap-2 pt-2">
                      <input
                        type="checkbox"
                        id="acceptTerms"
                        checked={acceptTerms}
                        onChange={(e) => setAcceptTerms(e.target.checked)}
                        className="w-4 h-4 rounded border-slate-700 bg-slate-800 text-primary focus:ring-primary"
                      />
                      <label htmlFor="acceptTerms" className="text-[10px] font-bold text-slate-400 uppercase">
                        I accept the <a href="/terms" target="_blank" className="text-primary hover:underline">Terms & Conditions</a>
                      </label>
                    </div>
                  </div>
                )}

                <button
                  type="submit"
                  disabled={isLoading}
                  className="w-full bg-primary text-black font-bold py-3 rounded mt-4 uppercase disabled:opacity-50"
                >
                  {isLoading ? 'Processing...' : userSubMode === 'signup' ? 'Create Account' : 'Login'}
                </button>
              </form>

              <div className="relative py-4">
                <div className="absolute inset-0 flex items-center"><div className="w-full border-t border-slate-700"></div></div>
                <div className="relative flex justify-center text-[8px] uppercase font-bold"><span className="bg-surface px-2 text-slate-500">Or Continue With</span></div>
              </div>

              <div className="space-y-2">
                <button
                  type="button"
                  onClick={handleGoogleLogin}
                  disabled={isLoading}
                  className="w-full bg-white text-slate-900 font-bold py-3 rounded flex items-center justify-center gap-3 uppercase disabled:opacity-50 hover:bg-slate-100 transition-colors"
                >
                  <svg className="w-4 h-4" viewBox="0 0 24 24">
                    <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" />
                    <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" />
                    <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" />
                    <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 12-4.53z" />
                  </svg>
                  Google
                </button>

                <button
                  type="button"
                  onClick={handleFacebookLogin}
                  disabled={isLoading}
                  className="w-full bg-[#1877F2] text-white font-bold py-3 rounded flex items-center justify-center gap-3 uppercase disabled:opacity-50 hover:bg-[#166fe5] transition-colors"
                >
                  <Facebook className="w-4 h-4" />
                  Facebook
                </button>
              </div>

              <div className="text-center pt-2">
                <button
                  type="button"
                  onClick={() => setUserSubMode(userSubMode === 'signup' ? 'login' : 'signup')}
                  className="text-[10px] text-slate-500 hover:text-primary uppercase font-bold"
                >
                  {userSubMode === 'signup' ? 'Already have an account? Login' : 'Need an account? Sign Up'}
                </button>
              </div>
            </>
          ) : (
            <>
              <form onSubmit={(e) => { e.preventDefault(); handleOwnerLogin(); }} className="space-y-4">
                <div className="bg-red-900/20 p-3 text-[10px] text-red-300 rounded border border-red-800 uppercase text-center">Partner Access</div>
                <div className="relative">
                  <Mail className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                  <input type="email" value={ownerEmail} onChange={(e) => setOwnerEmail(e.target.value)} placeholder="Email" className={inputClasses} />
                </div>
                <div className="relative">
                  <Lock className="w-4 h-4 absolute left-3 top-3 text-slate-400" />
                  <input type="password" value={ownerPassword} onChange={(e) => setOwnerPassword(e.target.value)} placeholder="Password" className={inputClasses} autoComplete="current-password" />
                </div>
                <button
                  type="submit"
                  className="w-full bg-slate-800 text-white font-bold py-3 rounded mt-4 uppercase hover:bg-slate-700 transition-colors"
                  disabled={isLoading}
                >
                  {isLoading ? 'Processing...' : 'Login'}
                </button>
              </form>

              <div className="relative py-4">
                <div className="absolute inset-0 flex items-center"><div className="w-full border-t border-slate-700"></div></div>
                <div className="relative flex justify-center text-[8px] uppercase font-bold"><span className="bg-surface px-2 text-slate-500">Or Commissioner Login</span></div>
              </div>

              <div className="space-y-2">
                <button
                  type="button"
                  onClick={handleGoogleLogin}
                  disabled={isLoading}
                  className="w-full bg-slate-800 text-white border border-slate-600 font-bold py-3 rounded flex items-center justify-center gap-3 uppercase hover:bg-slate-700 transition-colors"
                >
                  <svg className="w-4 h-4" viewBox="0 0 24 24">
                    <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" />
                    <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" />
                    <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" />
                    <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 12-4.53z" />
                  </svg>
                  Google
                </button>

                <button
                  type="button"
                  onClick={handleFacebookLogin}
                  disabled={isLoading}
                  className="w-full bg-[#1877F2] text-white font-bold py-3 rounded flex items-center justify-center gap-3 uppercase disabled:opacity-50 hover:bg-[#166fe5] transition-colors"
                >
