                        Points determine your rank in the <span className="text-primary font-bold italic">OlyBars Season Standings</span>. High-ranking members get first access to limited releases, exclusive League merchandise, and invitations to private Artesian parties.
                    </p>
                    <div className="flex flex-wrap gap-2">
                        {['Limited Merch', 'Event Invites', 'Early Access', 'Status'].map(tag => (
                            <span key={tag} className="bg-black/40 text-[10px] font-black uppercase tracking-widest text-primary px-3 py-1.5 rounded-full border border-primary/20">
                                {tag}
                            </span>
                        ))}
                    </div>
                </div>

                <footer className="mt-16 pt-12 border-t border-white/5 text-center">
                    <p className="text-[10px] text-slate-600 font-bold uppercase tracking-widest leading-loose">
                        Point values are subject to League governance.<br />
                        Abuse of the Pulse system leads to disqualification.
                    </p>
                </footer>
            </div>
        </div>
    );
};

export default PointsGuideScreen;

import React, { useState, useEffect, useMemo } from 'react';
import {
    ShieldCheck, Users, Trophy, ChevronRight, Mail, ExternalLink,
    Star, Loader2, Gamepad2, MapPin, Award, Zap, Clock,
    Sparkles, Anchor, Map, Info, Activity
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { Venue } from '../../../types';
import { fetchVenues } from '../../../services/venueService';
import { isVenueOpen } from '../../../utils/venueUtils';
import { SEO } from '../../../components/common/SEO';

// Assets
import heroArena from '../../../assets/hero-arena.png';
import leagueBadge from '../../../assets/league-badge.png';
import artieCoachBg from '../../../assets/artie-coach-bg.png';
import venuePartnerBg from '../../../assets/venue-partner-bg.png';

import { API_BASE_URL } from '../../../lib/api-config';

const AboutPage = () => {
    const navigate = useNavigate();
    const [showContact, setShowContact] = useState(false);
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [hpValue, setHpValue] = useState(''); // Honeypot
    const [loading, setLoading] = useState(false);
    const [venues, setVenues] = useState<Venue[]>([]);
    const [fetchingVenues, setFetchingVenues] = useState(true);

    useEffect(() => {
        const loadVenues = async () => {
            try {
                const data = await fetchVenues();
                setVenues(data);
            } catch (err) {
                console.error("Failed to load venues for handbook", err);
            } finally {
                setFetchingVenues(false);
            }
        };
        loadVenues();
    }, []);

    const buzzingVenues = useMemo(() =>
        venues.filter(v => v.status === 'buzzing' && v.isActive !== false).slice(0, 2),
        [venues]);

    const activeHappyHours = useMemo(() =>
        venues.filter(v => (v.deal || (v.flashBounties && v.flashBounties.length > 0)) && isVenueOpen(v)).slice(0, 3),
        [venues]);

    const handleSend = async () => {
        if (!email || !message) {
            alert("Please fill in both fields");
            return;
        }
        setLoading(true);
        try {
            const response = await fetch(`${API_BASE_URL}/requests`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    type: 'CONTACT',
                    contactEmail: email,
                    payload: { message },
                    _hp_id: hpValue // [SECURITY] Honeypot
                })
            });
            if (response.ok) {
                alert("Message Sent! Artie will get back to you soon.");
                setShowContact(false);
                setEmail('');
                setMessage('');
            } else {
                alert("Error sending message.");
            }
        } catch (e) {
            console.error(e);
            alert("Network error.");
        } finally {
            setLoading(false);
        }
