                    "@type": "BreadcrumbList",
                    "itemListElement": [
                        {
                            "@type": "ListItem",
                            "position": 1,
                            "name": "Home",
                            "item": "https://olybars.com/"
                        },
                        {
                            "@type": "ListItem",
                            "position": 2,
                            "name": "About",
                            "item": "https://olybars.com/about"
                        }
                    ]
                }}
            />
            {/* Structural SEO: Visual H1 is sometimes stylized, providing a clear reference point */}
