                        <div className="bg-surface border border-white/5 rounded-2xl p-5 space-y-3">
                            <p className="text-xs text-slate-400 mb-2">
                                {venue.physicalRoom === false
                                    ? `Scan a Vibe Spot at these partner bars to unlock the '${venue.scavengerHunts?.[0]?.badgeId || 'Maker'}' badge!`
                                    : "Our products are proudly poured at:"}
                            </p>
                            <div className="grid grid-cols-1 gap-2">
                                {venues.filter(v => v.carryingMakers?.includes(venue.id) || venue.scavengerHunts?.[0]?.partnerVenues.includes(v.id)).map(carrier => (
                                    <div key={carrier.id} onClick={() => navigate(`/venues/${carrier.id}`)} className="flex items-center justify-between p-3 bg-black/40 rounded-lg border border-white/5 cursor-pointer hover:bg-white/5 transition-colors group">
                                        <div className="flex items-center gap-3">
                                            <Beer className="w-4 h-4 text-slate-500 group-hover:text-primary transition-colors" />
                                            <span className="font-bold text-sm text-white">{carrier.name}</span>
                                        </div>
                                        <div className="flex items-center gap-2">
                                            {venue.physicalRoom === false && <Sparkles className="w-3 h-3 text-primary animate-pulse" />}
                                            <ChevronRight className="w-4 h-4 text-slate-700 group-hover:text-white" />
                                        </div>
                                    </div>
                                ))}
                                {venues.filter(v => v.carryingMakers?.includes(venue.id) || venue.scavengerHunts?.[0]?.partnerVenues.includes(v.id)).length === 0 && (
                                    <p className="text-[10px] text-slate-600 font-bold uppercase py-2">Distribution list updating...</p>
                                )}
                            </div>
                        </div>
                    </div>
                )}

                {/* Gallery */}
                <div className="space-y-4">
                    <h3 className="text-xs font-black text-slate-500 uppercase tracking-[0.3em] font-league italic">Vibe Gallery</h3>
                    <div className="bg-surface border border-white/5 rounded-2xl p-2 min-h-[150px]">
                        <VenueGallery photos={venue.photos} />
                    </div>
                </div>

                {/* Policies & Access Detail [NEW] */}
                {(venue.reservations || venue.hasPrivateRoom || (venue.privateSpaces && venue.privateSpaces.length > 0) || venue.openingTime) && (
                    <div className="space-y-4">
                        <h3 className="text-xs font-black text-slate-500 uppercase tracking-[0.3em] font-league italic">Policies & Reservations</h3>
                        <div className="bg-surface border border-white/5 rounded-2xl p-4 space-y-4">
                            {venue.openingTime && (
                                <div className="flex items-center gap-3">
                                    <Clock className="w-4 h-4 text-primary" />
                                    <div>
                                        <span className="text-[9px] font-black text-slate-500 uppercase tracking-widest">Opens At</span>
                                        <p className="text-xs font-bold text-white uppercase">{venue.openingTime}</p>
                                    </div>
                                </div>
                            )}
                            {venue.reservations && (
                                <div className="flex items-center gap-3">
                                    <Info className="w-4 h-4 text-primary" />
                                    <div>
                                        <span className="text-[9px] font-black text-slate-500 uppercase tracking-widest">Reservations</span>
                                        <p className="text-xs font-bold text-white uppercase">{venue.reservations}</p>
                                        {venue.reservationUrl && (
                                            <a
                                                href={venue.reservationUrl}
                                                target="_blank"
                                                rel="noopener noreferrer"
                                                className="mt-2 inline-flex items-center gap-2 bg-primary/20 hover:bg-primary/30 border border-primary/40 text-primary text-[10px] font-black uppercase px-3 py-1.5 rounded-lg transition-all"
                                            >
                                                <ExternalLink size={10} />
                                                Book a Table
                                            </a>
                                        )}
                                    </div>
                                </div>
                            )}
                            {(venue.hasPrivateRoom || (venue.privateSpaces && venue.privateSpaces.length > 0)) && (
                                <div className="flex items-center gap-3">
                                    <Key className="w-4 h-4 text-primary" />
                                    <div className="flex-1">
                                        <span className="text-[9px] font-black text-slate-500 uppercase tracking-widest">The Back Room</span>
                                        <p className="text-xs font-bold text-white uppercase">
                                            {venue.privateSpaces?.length
                                                ? `${venue.privateSpaces.length} Private Space${venue.privateSpaces.length > 1 ? 's' : ''} Available`
                                                : 'Private Space Available'}
                                        </p>
                                        {venue.privateSpaces?.length ? (
                                            <button
                                                onClick={() => navigate(`/back-room?venueId=${venue.id}`)}
                                                className="mt-2 inline-flex items-center gap-2 bg-primary/20 hover:bg-primary/30 border border-primary/40 text-primary text-[10px] font-black uppercase px-3 py-1.5 rounded-lg transition-all"
                                            >
                                                <ExternalLink size={10} />
                                                View Spaces
                                            </button>
                                        ) : (
                                            <p className="text-[9px] text-slate-400 italic mt-1">Direct booking available at venue</p>
                                        )}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                )}

                {/* Location Info / Conditional Navigation */}
                {venue.physicalRoom !== false && (
                    <div className="space-y-4">
                        <h3 className="text-xs font-black text-slate-500 uppercase tracking-[0.3em] font-league italic">Navigation</h3>
                        <div className="bg-surface border border-white/5 rounded-2xl p-4 flex justify-between items-center transition-all hover:border-primary/30 cursor-pointer"
                            onClick={() => venue.address && window.open(`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(venue.address)}`, '_blank')}>
                            <div className="flex items-center gap-3">
                                <div className="bg-slate-800 p-2.5 rounded-xl">
                                    <Navigation className="w-5 h-5 text-blue-400" />
                                </div>
                                <div className="flex flex-col">
                                    <span className="text-[10px] font-black text-slate-500 uppercase">Address</span>
                                    <p className="text-sm font-bold text-white uppercase tracking-tight">{venue.address || 'Olympia, WA'}</p>
                                </div>
                            </div>
                            <ChevronRight className="w-5 h-5 text-slate-700" />
                        </div>
                    </div>
                )}

                {/* [NEW] Owner Claim Section */}
                {(!venue.ownerId || isSystemAdmin(userProfile) || !userProfile || userProfile.uid === 'guest') && (
                    <div className="pt-16 mt-16 border-t border-white/5 text-center pb-8 sticky-claim-trigger">
                        <div className="inline-block p-4 bg-primary/10 rounded-3xl border border-primary/20 mb-6 group hover:scale-110 transition-transform duration-500">
                            <Crown className="w-10 h-10 text-primary" />
                        </div>
                        <h4 className="text-2xl font-black text-white uppercase font-league mb-3 tracking-tighter">Claim Your Venue</h4>
                        <p className="text-sm text-slate-400 font-medium mb-8 max-w-sm mx-auto leading-relaxed italic">
                            Are you the owner or manager of <span className="text-primary font-bold">{venue.name}</span>? Claim this listing to manage your vibe, menu, and events in the 98501.
                        </p>
                        <button
                            onClick={() => {
                                let url = '/partners/claim';
                                const params = new URLSearchParams();
                                if (venue.id) params.append('venueId', venue.id);
                                if (venue.googlePlaceId) params.append('placeId', venue.googlePlaceId);
                                params.append('name', venue.name);
                                if (venue.address) params.append('address', venue.address);
                                if (params.toString()) url += `?${params.toString()}`;
                                navigate(url);
                            }}
                            className="w-full sm:w-auto bg-primary text-black px-12 py-5 rounded-2xl font-black text-xs uppercase tracking-[0.2em] transition-all hover:scale-105 active:scale-95 shadow-2xl shadow-primary/30 font-league"
                        >
                            Claim this Bar
                        </button>
                    </div>
                )}
            </div>

            {/* [PHASE 1] Flight Builder Dock & Modal */}
            {flightItems.length > 0 && (
                <>
                    {/* Fixed Dock */}
                    <div className="fixed bottom-24 left-4 right-4 z-40 animate-in slide-in-from-bottom duration-300 md:bottom-6 md:left-1/2 md:-translate-x-1/2 md:w-full md:max-w-md">
                        <div className="bg-slate-900/90 backdrop-blur-md border border-primary/30 p-4 rounded-2xl shadow-2xl flex items-center justify-between">
                            <div className="flex items-center gap-3">
                                <div className="bg-primary text-black font-black w-8 h-8 rounded-lg flex items-center justify-center text-sm shadow-lg shadow-primary/20">
                                    {flightItems.length}
                                </div>
                                <div className="flex flex-col">
                                    <div className="flex items-center gap-1">
                                        <span className="text-[10px] font-black text-slate-500 uppercase tracking-widest">Building Flight</span>
                                        <button onClick={(e) => { e.stopPropagation(); navigate('/flight-school'); }} className="text-primary hover:text-white transition-colors">
                                            <HelpCircle size={10} />
                                        </button>
                                    </div>
                                    <span className="text-xs font-bold text-white uppercase">{4 - flightItems.length} slots remaining</span>
                                </div>
                            </div>
                            <button
                                onClick={() => setIsFlightModalOpen(true)}
                                className="bg-primary text-black px-6 py-2 rounded-xl font-black text-xs uppercase tracking-widest hover:bg-yellow-400 transition-colors shadow-lg shadow-primary/10"
                            >
                                View Stats
                            </button>
                        </div>
                    </div>

                    {/* Flight Modal */}
                    {isFlightModalOpen && (
                        <div className="fixed inset-0 z-[60] bg-black/90 backdrop-blur-sm flex items-center justify-center p-4 animate-in fade-in duration-200">
                            <div className="bg-slate-900 border border-white/10 w-full max-w-sm rounded-3xl overflow-hidden relative shadow-2xl">
                                <button
                                    onClick={() => setIsFlightModalOpen(false)}
                                    className="absolute top-4 right-4 text-slate-500 hover:text-white"
                                >
                                    <X size={24} />
                                </button>

                                <div className="p-8 text-center bg-slate-800/50">
                                    <Trophy className="w-12 h-12 text-primary mx-auto mb-4" />
                                    <h3 className="text-2xl font-black text-white uppercase font-league tracking-tighter mb-1">Your Flight Deck</h3>
                                    <p className="text-xs text-slate-400 font-bold uppercase tracking-widest">Calculated Experience</p>
                                </div>

                                <div className="p-6 space-y-6">
                                    {/* Stats Grid */}
                                    <div className="grid grid-cols-2 gap-4">
                                        <div className="bg-black/40 p-4 rounded-2xl border border-white/5 text-center">
                                            <p className="text-[10px] font-black text-slate-500 uppercase tracking-widest mb-1">Avg ABV</p>
                                            <p className={`text-3xl font-black font-league ${parseFloat(flightStats.abv as string) > 8.0 ? 'text-red-500' : 'text-white'}`}>
                                                {flightStats.abv}%
                                            </p>
                                        </div>
                                        <div className="bg-black/40 p-4 rounded-2xl border border-white/5 text-center">
                                            <p className="text-[10px] font-black text-slate-500 uppercase tracking-widest mb-1">Est. Cost</p>
                                            <p className="text-3xl font-black text-white font-league">$$</p>
                                        </div>
                                    </div>

                                    {/* Warnings */}
                                    {parseFloat(flightStats.abv as string) > 8.0 && (
                                        <div className="bg-red-900/20 border border-red-500/30 p-4 rounded-xl flex items-start gap-3">
                                            <AlertTriangle className="w-5 h-5 text-red-500 shrink-0" />
                                            <div>
                                                <p className="text-xs font-black text-red-400 uppercase tracking-wide mb-1">High Gravity Alert</p>
                                                <p className="text-[10px] text-red-300 leading-relaxed">
                                                    This flight packs a punch. Please drink responsibly and arrange a safe ride home.
                                                </p>
                                            </div>
                                        </div>
                                    )}

                                    {/* Items List */}
                                    <div className="space-y-2">
                                        {flightItems.map((item, idx) => (
                                            <div key={idx} className="flex justify-between items-center p-3 bg-white/5 rounded-xl border border-white/5">
                                                <span className="text-xs font-bold text-white">{item.name}</span>
                                                <div className="flex items-center gap-3">
                                                    <span className="text-[10px] font-mono text-slate-400">{item.stats.abv}%</span>
                                                    <button onClick={() => toggleFlightItem(item)} className="text-slate-600 hover:text-red-500">
                                                        <X size={14} />
                                                    </button>
                                                </div>
                                            </div>
                                        ))}
                                    </div>

                                    {/* Actions */}
                                    <button
                                        onClick={() => {
                                            if (!userProfile) {
                                                showToast("Please log in to save your flight!", "error");

                                                // Ideally, trigger login modal here if available
                                                return;
                                            }
                                            // TODO: Actual save logic to Firestore users/{uid}/flights
                                            showToast("Flight Saved to Profile! (Coming Soon: View Flights)", "success");
                                            setIsFlightModalOpen(false);
                                            setFlightItems([]);
                                        }}
                                        className="w-full bg-primary text-black font-black py-4 rounded-xl uppercase tracking-widest hover:scale-[1.02] active:scale-95 transition-all shadow-xl shadow-primary/20"
                                    >
                                        Confirm Selection
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                </>
            )}

            {/* Distance Warning Modal */}
            {venue && venue.location && (
                <ArtieDistanceWarningModal
                    isOpen={isWarningOpen}
                    onClose={() => setIsWarningOpen(false)}
                    distanceMiles={distanceInfo.miles}
                    walkTimeMins={distanceInfo.mins}
                    destinationName={venue.name}
                    destinationAddress={venue.address || ''}
                    destinationCoords={{ lat: venue.location.lat, lng: venue.location.lng }}
                    userZone={zones.user}
                    destinationZone={zones.destination}
                />
            )}

            {/* Gatekeeper Modal for Private Clubs */}
            <GatekeeperModal
                isOpen={isGatekeeperOpen}
                onClose={() => setIsGatekeeperOpen(false)}
                onAcknowledge={() => {
                    setIsMembershipVerified(true);
                    setIsGatekeeperOpen(false);
                }}
                venue={venue}
            />
        </div>
    );
};

export default VenueProfileScreen;

import React, { useState, useMemo } from 'react';
import { useNavigate, Link, useSearchParams } from 'react-router-dom';
import {
    Beer, Bot, ChevronRight, Clock, Crown, Filter, Flame, MapPin, Music, Navigation, Search, Sparkles, Star, Trophy, Users
} from 'lucide-react';
import { Venue, VenueType, SceneTag, VenueStatus } from '../../../types';
import { useGeolocation } from '../../../hooks/useGeolocation';
import { calculateDistance, metersToMiles } from '../../../utils/geoUtils';
import { isVenueOpen, getVenueStatus } from '../../../utils/venueUtils';
import { VenueGallery } from '../components/VenueGallery';
import { useToast } from '../../../components/ui/BrandedToast';

interface VenuesScreenProps {
    venues: Venue[];
    handleVibeCheck?: (v: Venue) => void;
    lastVibeChecks?: Record<string, number>;
    lastGlobalVibeCheck?: number;
}

type SortOption = 'alpha' | 'distance' | 'energy' | 'buzz';

export const VenuesScreen: React.FC<VenuesScreenProps> = ({ venues, handleVibeCheck, lastVibeChecks, lastGlobalVibeCheck }) => {
    const navigate = useNavigate();
    const { coords } = useGeolocation();
    const { showToast } = useToast();
    const [searchParams] = useSearchParams();
    const [searchQuery, setSearchQuery] = useState('');
    const [activeSort, setActiveSort] = useState<SortOption>('buzz');
    const [showOpenOnly, setShowOpenOnly] = useState(false);
    const [activeType, setActiveType] = useState<VenueType | 'all'>('all');
    const [activeTag, setActiveTag] = useState<string | null>(searchParams.get('filter') === 'makers' ? 'Makers' : null);

    // Rotation Logic (shifts every 5 minutes) ensures global fairness
    const rotationOffset = useMemo(() => {
        const rotationInterval = 5 * 60 * 1000;
        return Math.floor(Date.now() / rotationInterval);
    }, []);

    // Filter and Sort Logic
    const processedVenues = useMemo(() => {
        let result = venues.map(v => ({
            ...v,
            isOpen: isVenueOpen(v),
            hourStatus: getVenueStatus(v),
            distance: coords && v.location ? metersToMiles(calculateDistance(coords.latitude, coords.longitude, v.location.lat, v.location.lng)) : null
        })).filter(v => v.isActive !== false); // Filter out Soft Deleted / Archived venues

        // 1. Search Filter
        if (searchQuery) {
            const q = searchQuery.toLowerCase();
            result = result.filter(v =>
                (v.name && v.name.toLowerCase().includes(q)) ||
                (v.address && v.address.toLowerCase().includes(q)) ||
                (v.venueType && v.venueType.replace(/_/g, ' ').toLowerCase().includes(q)) ||
                (v.vibe && v.vibe.toLowerCase().includes(q)) ||
                // Keyword matches for amenities [NEW]
                (q.includes('dog') && v.isDogFriendly) ||
                (q.includes('family') && v.isAllAges) ||
                (q.includes('all ages') && v.isAllAges) ||
                (q.includes('kids') && v.isAllAges) ||
                (q.includes('outdoor') && v.hasOutdoorSeating) ||
                (q.includes('patio') && v.hasOutdoorSeating) ||
                (q.includes('private') && (v.hasPrivateRoom || (v.privateSpaces && v.privateSpaces.length > 0))) ||
                (q.includes('room') && (v.privateSpaces && v.privateSpaces.length > 0)) ||
                (q.includes('back room') && (v.privateSpaces && v.privateSpaces.length > 0))
            );
        }

        // 2. Open Only Filter
        if (showOpenOnly) {
            result = result.filter(v => v.isOpen);
        }

        // 3. Venue Type Filter (Primary Toggle)
        if (activeType !== 'all') {
            result = result.filter(v => v.venueType === activeType);
        }

        // 4. Tag Filter (Vibe Tags & Functional Tags)
        if (activeTag) {
            if (activeTag === 'Deals') result = result.filter(v => !!v.deal || (v.flashBounties && v.flashBounties.length > 0));
            else if (activeTag === 'Makers') {
                result = result.filter(v =>
                    v.isHQ ||
                    v.isLocalMaker ||
                    v.venueType === 'brewery_taproom'
                );
            }
            else if (activeTag === 'Tasting') {
                result = result.filter(v =>
                    v.venueType === 'winery_tasting' ||
                    v.makerType === 'Winery' ||
                    v.makerType === 'Distillery' ||
                    v.sceneTags?.includes('wine_focus')
                );
            }
            else if (activeTag === 'Trivia') result = result.filter(v => v.leagueEvent === 'trivia');
            // Check Vibe Tags
            else {
                const sceneTagValue = activeTag.toLowerCase().replace(/ /g, '_') as SceneTag; // rough mapping, better to specific map
                // Helper map for display -> value
                const TAG_MAP: Record<string, SceneTag> = {
                    'Dive': 'dive',
                    'Speakeasy': 'speakeasy',
                    'Sports': 'sports',
                    'Tiki': 'tiki_theme',
                    'Wine': 'wine_focus',
                    'Cocktails': 'cocktail_focus',
                    'LGBTQ+': 'lgbtq',
                    'Patio': 'patio_garden'
                };
                const targetScene = TAG_MAP[activeTag];
                if (targetScene) {
                    result = result.filter(v => v.sceneTags?.includes(targetScene));
                }
            }
        }

        // 4. Global Visibility
        result = result.filter(v => v.tier_config?.is_directory_listed !== false);

        // 5. Sorting
        result.sort((a, b) => {
            if (activeSort === 'alpha') {
                return a.name.localeCompare(b.name);
            }
            if (activeSort === 'distance') {
                const distA = a.distance ?? 999;
                const distB = b.distance ?? 999;
                return distA - distB;
            }
            if (activeSort === 'energy') {
                const order: Record<VenueStatus, number> = { packed: 0, buzzing: 1, chill: 2, dead: 3 };
                return order[a.status] - order[b.status];
            }
            if (activeSort === 'buzz') {
                // Priority 0: Partner Exposure Equity (League Members)
                const isAPartner = a.isPaidLeagueMember;
                const isBPartner = b.isPaidLeagueMember;
                if (isAPartner !== isBPartner) return isAPartner ? -1 : 1;

                // Priority 1: Has Deal?
                const aHasDeal = !!(a.deal || (a.flashBounties && a.flashBounties.length > 0));
                const bHasDeal = !!(b.deal || (b.flashBounties && b.flashBounties.length > 0));

                if (aHasDeal && !bHasDeal) return -1;
                if (!aHasDeal && bHasDeal) return 1;

                // Priority 2: Tie-Break with Rotation
                if (isAPartner && isBPartner) {
                    const aHash = a.id.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
                    const bHash = b.id.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
                    return ((aHash + rotationOffset) % 100) - ((bHash + rotationOffset) % 100);
                }

                // Fallback to Status
                const order: Record<VenueStatus, number> = { packed: 0, buzzing: 1, chill: 2, dead: 3 };
                return order[a.status] - order[b.status];
            }
            return 0;
        });

        return result;
    }, [venues, searchQuery, showOpenOnly, activeTag, activeSort, coords]);

    return (
        <div className="bg-background min-h-screen pb-32 font-body text-slate-100">
            {/* Header & Search */}
            <div className="p-6 space-y-4">
                <div className="flex flex-col items-center gap-1 mb-2">
                    <h1 className="text-4xl font-black text-white tracking-widest font-league uppercase italic leading-none">
                        BAR <span className="text-primary">DIRECTORY</span>
                    </h1>
                    <p className="text-[10px] text-slate-500 font-bold uppercase tracking-[0.2em] mt-1 italic">The OlyBars Index</p>
                </div>

                <div className="relative group">
                    <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-500 group-focus-within:text-primary transition-colors" />
                    <input
                        type="text"
                        placeholder="Search by name, vibe, or address..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="w-full bg-surface border-2 border-slate-700 rounded-2xl py-4 pl-12 pr-4 text-white font-bold placeholder:text-slate-600 focus:border-primary outline-none transition-all shadow-xl font-body"
                    />
                </div>

                {/* Type Toggles (Primary) */}
                <div className="flex gap-2 overflow-x-auto pb-2 scrollbar-hide">
                    {[
                        { id: 'all', label: 'All' },
                        { id: 'bar_pub', label: 'Bars' },
                        { id: 'restaurant_bar', label: 'Rest & Bar' },
                        { id: 'brewery_taproom', label: 'Breweries' },
                        { id: 'brewpub', label: 'Brewpubs' },
                        { id: 'lounge_club', label: 'Lounges' },
                        { id: 'arcade_bar', label: 'Arcades' }
                    ].map(type => (
                        <button
                            key={type.id}
                            onClick={() => setActiveType(type.id as VenueType | 'all')}
                            className={`px-3 py-1.5 rounded-lg text-[10px] font-black uppercase tracking-widest transition-all whitespace-nowrap border ${activeType === type.id
                                ? 'bg-white text-black border-white'
                                : 'bg-transparent text-slate-500 border-slate-800 hover:border-slate-600'
                                }`}
                        >
                            {type.label}
                        </button>
                    ))}
                </div>

                {/* Sorting & Quick Filters */}
                <div className="flex flex-col gap-4">
                    <div className="flex items-center gap-2 overflow-x-auto pb-2 scrollbar-hide">
                        <button
                            onClick={() => setActiveSort('buzz')}
                            className={`flex items-center gap-1 px-4 py-2 rounded-xl border-2 font-black text-[10px] uppercase tracking-widest transition-all whitespace-nowrap font-league ${activeSort === 'buzz' ? 'bg-primary text-black border-black shadow-[2px_2px_0px_0px_#fff]' : 'bg-surface text-slate-400 border-slate-800'}`}
                        >
                            <Flame size={12} fill="currentColor" /> Buzz Clock
                        </button>
                        <button
                            onClick={() => setActiveSort('alpha')}
                            className={`px-4 py-2 rounded-xl border-2 font-black text-[10px] uppercase tracking-widest transition-all whitespace-nowrap font-league ${activeSort === 'alpha' ? 'bg-primary text-black border-black shadow-[2px_2px_0px_0px_#fff]' : 'bg-surface text-slate-400 border-slate-800'}`}
                        >
                            Alphabetical
                        </button>
                        <button
                            onClick={() => setActiveSort('distance')}
                            className={`px-4 py-2 rounded-xl border-2 font-black text-[10px] uppercase tracking-widest transition-all whitespace-nowrap font-league ${activeSort === 'distance' ? 'bg-primary text-black border-black shadow-[2px_2px_0px_0px_#fff]' : 'bg-surface text-slate-400 border-slate-800'}`}
                        >
                            Nearest
                        </button>
                        <button
                            onClick={() => setActiveSort('energy')}
                            className={`px-4 py-2 rounded-xl border-2 font-black text-[10px] uppercase tracking-widest transition-all whitespace-nowrap font-league ${activeSort === 'energy' ? 'bg-primary text-black border-black shadow-[2px_2px_0px_0px_#fff]' : 'bg-surface text-slate-400 border-slate-800'}`}
                        >
                            Vibe Check
                        </button>
                    </div>

                    <div className="flex items-center justify-between">
                        <div className="flex gap-2">
                            {['Makers', 'Tasting', 'Trivia', 'Deals', 'Dive', 'Speakeasy', 'Sports', 'Patio', 'Cocktails', 'Wine', 'Tiki', 'LGBTQ+'].map(tag => (
                                <button
                                    key={tag}
                                    onClick={() => setActiveTag(activeTag === tag ? null : tag)}
                                    className={`px-3 py-1.5 rounded-full text-[9px] font-black uppercase tracking-wider border transition-all whitespace-nowrap ${activeTag === tag ? 'bg-primary/20 text-primary border-primary' : 'bg-transparent text-slate-500 border-slate-800'}`}
                                >
                                    {tag}
                                </button>
                            ))}
                        </div>
                        <button
                            onClick={() => setShowOpenOnly(!showOpenOnly)}
                            className={`flex items-center gap-2 px-3 py-1.5 rounded-full text-[9px] font-black uppercase tracking-wider border transition-all ${showOpenOnly ? 'bg-green-500/20 text-green-400 border-green-500' : 'bg-transparent text-slate-500 border-slate-800'}`}
                        >
                            <Clock size={12} />
                            Open Now
                        </button>
                    </div>
                </div>
            </div>

            {/* Results */}
            <div className="px-6 space-y-4">
                {processedVenues.length === 0 ? (
                    <div className="space-y-6">
                        <div className="text-center py-20 bg-surface/30 rounded-3xl border-2 border-dashed border-slate-800">
                            <Sparkles className="w-12 h-12 text-slate-700 mx-auto mb-4" />
                            <p className="text-slate-500 font-bold uppercase tracking-widest font-league italic">No spots found in this vibe</p>
                        </div>

                        {/* Exposure Equity: Rotating Partner Fallback */}
                        <div className="bg-primary/5 border border-primary/20 rounded-2xl p-6">
                            <div className="flex items-center gap-2 mb-4 justify-center">
                                <Trophy className="w-5 h-5 text-primary" />
                                <h4 className="text-sm font-black text-primary uppercase tracking-widest font-league">League Partners</h4>
                            </div>

                            <div className="grid grid-cols-1 gap-3">
                                {[...venues]
                                    .filter(v => v.isPaidLeagueMember && v.isActive !== false)
                                    .map((v, i, arr) => {
                                        const shiftedIndex = (i + (rotationOffset % (arr.length || 1))) % (arr.length || 1);
                                        return arr[shiftedIndex];
                                    })
                                    .slice(0, 3)
                                    .map(v => (
                                        <Link
                                            key={`fallback-${v.id}`}
                                            to={`/venues/${v.id}`}
                                            className="w-full bg-slate-900/50 border border-white/5 rounded-xl p-4 flex justify-between items-center group/item hover:bg-slate-900 transition-colors"
                                        >
                                            <div className="flex items-center gap-3">
                                                <div className="bg-primary/20 p-2 rounded-lg group-hover/item:scale-110 transition-transform">
                                                    <Star className="w-4 h-4 text-primary fill-primary" />
                                                </div>
                                                <div className="text-left">
                                                    <h5 className="text-sm font-black text-white uppercase italic tracking-wide">{v.name}</h5>
                                                    <p className="text-[10px] text-slate-500 font-bold uppercase">{v.vibe}</p>
                                                </div>
                                            </div>
                                            <ChevronRight className="w-4 h-4 text-slate-600 group-hover/item:text-primary transition-all" />
                                        </Link>
                                    ))
                                }
                            </div>
                            <p className="mt-4 text-[9px] text-slate-600 font-bold uppercase tracking-[0.2em] text-center italic">
                                Rotating exposure for OlyBars partners
                            </p>
                        </div>
                    </div>
                ) : (
                    processedVenues.map(venue => (
                        <div
                            key={venue.id}
                            className="bg-surface border-2 border-slate-700 rounded-3xl overflow-hidden hover:border-primary transition-all group shadow-2xl relative"
                        >
                            <div className="p-6">
                                <div className="flex justify-between items-start mb-4">
                                    <div className="flex-1">
                                        <div className="flex items-center gap-2 mb-1">
                                            <Link to={`/venues/${venue.id}`} className="hover:text-primary transition-colors">
                                                <h3 className="text-2xl font-black text-white font-league uppercase italic leading-none group-hover:text-primary transition-colors">
                                                    {venue.name}
                                                </h3>
                                            </Link>
                                            {venue.isPaidLeagueMember && (
                                                <div className="bg-primary px-2 py-0.5 rounded transform -skew-x-12">
                                                    <span className="text-black text-[8px] font-black uppercase italic">PARTNER</span>
                                                </div>
                                            )}
                                        </div>
                                        {/* Google Rating Badge */}
                                        {venue.googleRating && (
                                            <div className="flex items-center gap-1.5 mb-2">
                                                <Star size={12} className="text-yellow-400 fill-yellow-400" />
                                                <span className="text-xs font-black text-white">{venue.googleRating}</span>
                                                <span className="text-[10px] text-slate-500 font-bold">({venue.googleReviewCount})</span>
                                            </div>
                                        )}
                                        <div className="flex items-center gap-2 text-[10px] font-bold text-slate-500 uppercase tracking-widest font-body">
                                            <span>{venue.venueType.replace(/_/g, ' ')}</span>
                                            <span>•</span>
                                            <span className="italic">"{venue.vibe}"</span>
                                        </div>
                                    </div>

                                    <div className="text-right">
                                        <div className={`text-[10px] font-black px-3 py-1 rounded-full border mb-2 inline-block font-league uppercase tracking-widest ${venue.hourStatus === 'open' ? 'bg-green-500/10 text-green-400 border-green-400/30' : venue.hourStatus === 'last_call' ? 'bg-red-600/20 text-red-400 border-red-500 animate-pulse' : 'bg-red-500/10 text-red-400 border-red-400/30'}`}>
                                            {venue.hourStatus === 'open' ? 'Open Now' : venue.hourStatus === 'last_call' ? 'LAST CALL 🕒' : 'Closed'}
                                        </div>
                                        {venue.distance !== null && (
                                            <div className="flex items-center justify-end gap-1 text-[10px] font-black text-primary font-league uppercase">
                                                <Navigation size={10} strokeWidth={3} />
                                                {venue.distance.toFixed(1)} mi
                                            </div>
                                        )}
                                    </div>
                                </div>

                                <div className="space-y-3">
                                    {venue.deal && (
                                        <div className="bg-primary/5 border border-primary/20 rounded-xl p-3 flex items-center gap-3">
                                            <Beer size={16} className="text-primary" />
                                            <div>
                                                <p className="text-[10px] font-black text-primary uppercase leading-none mb-1 font-league">Featured Deal</p>
                                                <p className="text-xs font-bold text-white font-body">{venue.deal}</p>
                                            </div>
                                        </div>
                                    )}

                                    {venue.leagueEvent && (
                                        <Link to={`/venues/${venue.id}`} className="block bg-slate-800/50 border border-slate-700/50 rounded-xl p-3 flex items-center justify-between group/event cursor-pointer hover:bg-slate-800 transition-colors">
                                            <div className="flex items-center gap-3">
                                                <Trophy size={16} className="text-primary" />
                                                <div>
                                                    <p className="text-[10px] font-black text-slate-500 uppercase leading-none mb-1 font-league">Tonight's Play</p>
                                                    <p className="text-xs font-bold text-white uppercase font-body">{venue.leagueEvent}</p>
                                                </div>
                                            </div>
                                            <ChevronRight size={16} className="text-slate-600 group-hover/event:text-primary transition-colors" />
                                        </Link>
                                    )}

                                    <div className="mt-4">
                                        <p className="text-[9px] font-black text-slate-500 uppercase tracking-[0.2em] mb-3 font-league italic">Venue Gallery</p>
                                        <VenueGallery photos={venue.photos} />
                                    </div>
                                </div>

                                <div className="mt-6 pt-6 border-t border-slate-800 flex items-center justify-between">
                                    <div className="flex-1 flex items-center justify-between gap-4">
                                        <div className="flex gap-4">
                                            <div className="flex items-center gap-1.5 text-[10px] font-black text-slate-500 uppercase font-league">
                                                <Music size={12} strokeWidth={3} />
                                                {venue.leagueEvent === 'karaoke' ? 'Karaoke High' : 'Vibe Varies'}
                                            </div>
                                            <div className="flex items-center gap-1.5 text-[10px] font-black text-slate-500 uppercase font-league">
                                                <MapPin size={12} strokeWidth={3} />
                                                {venue.address ? venue.address.split(',')[0] : 'Downtown Oly'}
                                            </div>
                                        </div>

                                        <div className="flex items-center gap-3">
                                            <button
                                                onClick={(e) => {
                                                    e.stopPropagation();
                                                    showToast('Find the physical Vibe Spot QR code inside ' + venue.name + ' to report a vibe.', 'info');
                                                }}
                                                className={`px-3 py-1.5 rounded-lg font-black text-[9px] uppercase tracking-widest transition-all border outline-none bg-primary/5 border-primary/20 text-primary hover:bg-primary/20`}
                                            >
                                                <span className="flex flex-col items-center">
                                                    <span className="flex items-center gap-1">
                                                        <Users size={10} strokeWidth={3} /> Vibe Check (+5)
                                                    </span>
                                                    <span className="text-[6px] opacity-60">SCAN QR REQUIRED</span>
                                                </span>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useGeolocation } from '../../../hooks/useGeolocation';
import { calculateDistance } from '../../../utils/geoUtils';
import { Venue, VenueStatus, GameStatus } from '../../../types';
import { MapPin, Loader2, AlertTriangle, CheckCircle2, ArrowRight } from 'lucide-react';
import { VibeCheckModal } from '../../venues/components/VibeCheckModal';

interface QRVibeCheckScreenProps {
    venues: Venue[];
    handleVibeCheck: (v: Venue, status: VenueStatus, hasConsent: boolean, photoUrl?: string, verificationMethod?: 'gps' | 'qr', gameStatus?: Record<string, GameStatus>, soberCheck?: { isGood: boolean; reason?: string }) => void;
}

export const QRVibeCheckScreen: React.FC<QRVibeCheckScreenProps> = ({ venues, handleVibeCheck }) => {
    const { venueId } = useParams<{ venueId: string }>();
    const navigate = useNavigate();
    const { coords, loading: geoLoading, error: geoError, requestLocation } = useGeolocation();
    const [venue, setVenue] = useState<Venue | null>(null);
    const [distance, setDistance] = useState<number | null>(null);
    const [isAtVenue, setIsAtVenue] = useState(false);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        const found = venues.find(v => v.id === venueId);
        if (found) {
            setVenue(found);
        }
    }, [venueId, venues]);

    useEffect(() => {
        if (venue && coords && venue.location) {
            const dist = calculateDistance(coords.latitude, coords.longitude, venue.location.lat, venue.location.lng);
            setDistance(dist);
            // 100 meters tolerance
            setIsAtVenue(dist <= 100);
        }
    }, [venue, coords]);

    const handleConfirmVibe = (v: Venue, status: VenueStatus, hasConsent: boolean, photoUrl?: string, verificationMethod?: 'gps' | 'qr', gameStatus?: Record<string, GameStatus>, soberCheck?: { isGood: boolean; reason?: string }) => {
        // Pass 'qr' as verification method
        handleVibeCheck(v, status, hasConsent, photoUrl, verificationMethod || 'qr', gameStatus, soberCheck);
        setShowModal(false);
        navigate(`/venues/${v.id}`); // Redirect to venue after
    };

    if (!venue) {
        return (
            <div className="min-h-screen bg-[#0f172a] flex items-center justify-center p-6 text-center">
                <div className="bg-slate-900 border border-white/10 p-8 rounded-2xl max-w-sm w-full">
                    <AlertTriangle className="w-12 h-12 text-red-500 mx-auto mb-4" />
                    <h2 className="text-xl font-black text-white uppercase font-league">Invalid QR Code</h2>
                    <p className="text-slate-400 text-sm mt-2">This code does not match any active venue.</p>
                    <button onClick={() => navigate('/')} className="mt-6 w-full bg-slate-800 py-3 rounded-xl font-bold uppercase text-xs">Return Home</button>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#0f172a] text-white p-6 flex flex-col items-center justify-center font-sans">
            <div className="max-w-md w-full space-y-8">
                <div className="text-center space-y-2">
                    <div className="w-20 h-20 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-6 border border-primary/20">
                        <MapPin className="w-10 h-10 text-primary animate-bounce" />
                    </div>
                    <p className="text-primary font-black uppercase tracking-widest text-xs">Vibe Spot Detected</p>
                    <h1 className="text-4xl font-black font-league uppercase italic leading-none">{venue.name}</h1>
                </div>

                <div className="bg-slate-900 border border-white/10 rounded-2xl p-6 shadow-2xl relative overflow-hidden">
                    {/* Background Glow */}
                    <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-primary to-transparent opacity-50" />

                    {geoLoading ? (
                        <div className="py-12 text-center space-y-4">
                            <Loader2 className="w-8 h-8 text-primary animate-spin mx-auto" />
                            <p className="text-slate-400 text-xs font-bold uppercase tracking-widest">Triangulating Position...</p>
                        </div>
                    ) : distance !== null ? (
                        <div className="text-center space-y-6">
                            {isAtVenue ? (
                                <>
                                    <div className="bg-green-500/10 border border-green-500/20 p-4 rounded-xl">
                                        <CheckCircle2 className="w-8 h-8 text-green-500 mx-auto mb-2" />
                                        <p className="text-green-400 font-black uppercase tracking-widest text-xs">Location Verified</p>
                                        <p className="text-white font-bold text-lg mt-1 font-mono">{Math.round(distance)}m Away</p>
                                    </div>
                                    <p className="text-slate-400 text-sm">You are on-site! Claim your verification check.</p>
                                    <button
                                        onClick={() => setShowModal(true)}
                                        className="w-full bg-primary text-black font-black py-4 rounded-xl uppercase tracking-widest text-lg shadow-lg hover:bg-yellow-400 transition-colors flex items-center justify-center gap-2"
                                    >
                                        Verify & Vibe <ArrowRight className="w-5 h-5" />
                                    </button>
                                </>
                            ) : (
                                <>
                                    <div className="bg-red-500/10 border border-red-500/20 p-4 rounded-xl">
                                        <AlertTriangle className="w-8 h-8 text-red-500 mx-auto mb-2" />
                                        <p className="text-red-400 font-black uppercase tracking-widest text-xs">Too Far Away</p>
                                        <p className="text-white font-bold text-lg mt-1 font-mono">{Math.round(distance)}m Away</p>
                                        <p className="text-[10px] text-red-300 mt-2">Must be within 100m</p>
                                    </div>
                                    <button
                                        onClick={requestLocation}
                                        className="w-full bg-slate-800 text-white font-black py-4 rounded-xl uppercase tracking-widest text-xs hover:bg-slate-700 transition-colors"
                                    >
                                        Retry Location
                                    </button>
                                </>
                            )}
                        </div>
                    ) : (
                        <div className="text-center py-8">
                            <p className="text-slate-400 text-xs mb-4">Location permission required to verify physical presence.</p>
                            <button
                                onClick={requestLocation}
                                className="bg-primary text-black font-bold px-6 py-2 rounded-full uppercase text-xs"
                            >
                                Enable Location
                            </button>
                        </div>
                    )}
                </div>

                <p className="text-center text-[10px] text-slate-600 font-bold uppercase tracking-widest">
                    OlyBars Verification Protocols v1.0
                </p>
            </div>

            <VibeCheckModal
                isOpen={showModal}
                onClose={() => setShowModal(false)}
                venue={venue}
                onConfirm={handleConfirmVibe}
            // No clock-in prompt here, strict focus on Vibe Check for this flow?
            // Or maybe allowing it.
            />
        </div>
    );
};

import { useState } from 'react';
import { httpsCallable } from 'firebase/functions';
import { functions } from '../lib/firebase';

import { API_BASE_URL } from '../lib/api-config';

export interface Message {
    role: 'user' | 'model';
    content: string;
}

export function useArtie() {
    const [messages, setMessages] = useState<Message[]>([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);

    const sendMessage = async (prompt: string, userId?: string, userRole?: string, hpValue?: string) => {
        if (!prompt.trim()) return;

        setIsLoading(true);
        setError(null);

        // Optimistic update: Add user message immediately
        const userMessage: Message = { role: 'user', content: prompt };
        const newHistory = [...messages, userMessage];
        setMessages(newHistory);

        try {

            // Convert internal message format to the simple role/content pairs Artie expects
            const historyForArtie = messages.map(m => ({
                role: m.role,
                content: m.content
            })).slice(-10);

            const response = await fetch(`${API_BASE_URL}/chat`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    question: prompt,
                    history: historyForArtie,
                    userId,
                    userRole,
                    venueId: (window as any)._artie_venue_id || undefined, // Allow global override if needed
                    _hp_id: hpValue // [SECURITY] Honeypot detection
                })
            });

            if (!response.ok) {
                throw new Error(`Artie connection failed: ${response.statusText}`);
            }

            const contentType = response.headers.get('Content-Type');

            if (contentType?.includes('application/json')) {
                // Handle fallback JSON response (e.g. triage/safety/banned)
                const result = await response.json();
                // [FIX] Ensure content is always a string to prevent client crashes (TypeError: split is not a function)
                let contentStr = result.data;
                if (typeof contentStr !== 'string') {
                    contentStr = JSON.stringify(contentStr);
                }
                const modelMessage: Message = { role: 'model', content: contentStr };
                setMessages(prev => [...prev, modelMessage]);
            } else {
                // Handle streaming response
                const reader = response.body?.getReader();
                const decoder = new TextDecoder();
                let accumulatedContent = '';

                // Add empty model message to start streaming into
                setMessages(prev => [...prev, { role: 'model', content: '' }]);

                if (reader) {
                    try {
                        while (true) {
                            const { done, value } = await reader.read();
                            if (done) break;

                            const chunk = decoder.decode(value, { stream: true });
                            accumulatedContent += chunk;

                            setMessages(prev => {
                                const newMessages = [...prev];
                                const lastMsg = newMessages[newMessages.length - 1];
                                if (lastMsg && lastMsg.role === 'model') {
                                    lastMsg.content = accumulatedContent;
                                }
                                return newMessages;
                            });
                        }
                    } catch (streamErr) {
                        // [STREAM_RESILIENCE] Gracefully handle network/stream interruptions
                        console.warn("Artie stream interrupted. Keeping partial response.", streamErr);
                        // The loop breaks, we keep what we have in accumulatedContent
                    } finally {
                        reader.releaseLock();
                    }
                }
            }

        } catch (err: any) {
            console.error("Artie hook failure:", err);
            setError(err.message || "Artie is having trouble connecting to the hive mind.");
        } finally {
            setIsLoading(false);
        }
    };

    const clearHistory = () => setMessages([]);

    return {
        messages,
        sendMessage,
        isLoading,
        error,
        clearHistory
    };
}

import { useState, useCallback } from 'react';
import { QuickReplyOption } from '../components/artie/QuickReplyChips';
import { VenueOpsService } from '../services/VenueOpsService';

// 1. State Definitions
export type ArtieOpsState =
    | 'idle'
    | 'selecting_skill'
    | 'flash_deal_init_method' // "I have a deal" vs "Help me"
    | 'flash_deal_input'
    | 'flash_deal_time_check'
    | 'event_input'
    | 'play_input'
    | 'social_post_input'
    | 'email_draft_input'
    | 'calendar_post_input'
    | 'website_content_input'
    | 'image_gen_purpose'
    | 'image_gen_goal'
    | 'image_gen_event'
    | 'image_gen_audience'
    | 'image_gen_specials'
    | 'image_gen_context'
    | 'post_image_gen'
    | 'confirm_action'
    | 'completed';

export interface ArtieMessage {
    id: string;
    role: 'artie' | 'user';
    text: string;
    timestamp: number;
    imageUrl?: string;
}

// 2. Regulatory Guardrails (LCB Compliance)
const LCB_FORBIDDEN_TERMS = ['free alcohol', 'free beer', 'free shots', 'free drinks', 'unlimited', 'bottomless', 'complimentary', 'giveaway', '0.00', '$0'];
const ALCOHOL_TERMS = ['beer', 'wine', 'shots', 'cocktails', 'drinks', 'booze', 'ipa', 'stout', 'pilsner', 'mimosas', 'tequila', 'whiskey', 'vodka', 'gin', 'rum'];

export const useArtieOps = () => {
    const [opsState, setOpsState] = useState<ArtieOpsState>('idle');
    const [messages, setMessages] = useState<ArtieMessage[]>([]);
    const [currentBubbles, setCurrentBubbles] = useState<QuickReplyOption[]>([]);
    const [draftData, setDraftData] = useState<any>({});
    const [isLoading, setIsLoading] = useState(false);
    const [venue, setVenue] = useState<any>(null);

    // 2. Fetch Venue Context
    const fetchVenue = useCallback(async (venueId: string) => {
        if (!venueId) return;
        try {
            const { db } = await import('../lib/firebase');
            const { doc, getDoc } = await import('firebase/firestore');
            const docRef = doc(db, 'venues', venueId);
            const docSnap = await getDoc(docRef);
            if (docSnap.exists()) {
                setVenue({ id: docSnap.id, ...docSnap.data() });
            }
        } catch (err) {
            console.error("Artie failed to load venue context:", err);
        }
    }, []);

    // 3. The Compliance Engine
    const validateLCBCompliance = useCallback((text: string): { valid: boolean; reason?: string } => {
        const lowerText = text.toLowerCase();

        // Check for "Free Alcohol" specifically (Combinations)
        const hasForbidden = LCB_FORBIDDEN_TERMS.some(term => lowerText.includes(term));
        const hasAlcohol = ALCOHOL_TERMS.some(word => lowerText.includes(word));

        // Rule: "Free" + Alcohol Context
        if (lowerText.includes('free') && hasAlcohol) {
            return {
                valid: false,
                reason: "I can't post that. Washington LCB regulations prohibit advertising 'free' alcohol. Try a specific price (e.g., '$1 Shots')."
            };
        }

        // Rule: "Unlimited" / "Bottomless"
        if (lowerText.includes('unlimited') || lowerText.includes('bottomless')) {
            return {
                valid: false,
                reason: "I can't post that. 'Unlimited' or 'Bottomless' drink offers are prohibited by LCB rules."
            };
        }

        return { valid: true };
    }, []);

    // 4. Time/Schedule Validator
    const validateSchedule = useCallback(async (timeISO: string, duration: number) => {
        // Mock venue for check - in real app would get from context
        const mockVenue = { partnerConfig: { tier: 'FREE', flashBountiesUsed: 0 } } as any;
        const check = await VenueOpsService.validateSlot(mockVenue, new Date(timeISO).getTime(), duration);
        return check;
    }, []);


    // 5. The Skill State Machine
    const processAction = useCallback(async (action: string, payload?: string, venueId?: string) => {
        // Auto-fetch if venueId provided and not loaded
        if (venueId && !venue) {
            await fetchVenue(venueId);
        }
        const newMessage: ArtieMessage = {
            id: Date.now().toString(),
            role: 'artie',
            text: '',
            timestamp: Date.now(),
        };

        // Helper to add user message before Artie responds
        const addUserMessage = (text: string) => {
            setMessages(prev => [...prev, { id: `u-${Date.now()}`, role: 'user', text, timestamp: Date.now() }]);
        };

        switch (action) {
            case 'START_SESSION':
                setOpsState('selecting_skill');
                newMessage.text = "Welcome back! I'm ready to help. What's the mission?";
                setMessages([newMessage]);
                setCurrentBubbles([
                    { id: '1', label: 'Flash Bounty', value: 'skill_flash_deal', icon: '⚡' },
                    { id: '2', label: 'Add Event', value: 'skill_add_event', icon: '📅' },
                    { id: '3', label: 'Social Post', value: 'skill_social_post', icon: '📱' },
                    { id: '4', label: 'Draft Email', value: 'skill_email_draft', icon: '✉️' },
                    { id: '5', label: 'Calendar Post', value: 'skill_calendar_post', icon: '🗓️' },
                    { id: '6', label: 'Web Content', value: 'skill_website_content', icon: '🌐' },
                    { id: '7', label: 'Gen Image', value: 'skill_generate_image', icon: '🎨' }
                ]);
                break;

            // --- SKILL: Flash Bounty INTRO ---
            case 'skill_flash_deal':
                addUserMessage('Flash Bounty');
                setOpsState('flash_deal_init_method');
                newMessage.text = "Let's fill some seats. Do you have a deal in mind, or want some ideas?";
                setMessages(prev => [...prev, newMessage]);
                setCurrentBubbles([
                    { id: 'have_deal', label: 'I have a deal', value: 'method_manual_input', icon: '📝' },
                    { id: 'need_ideas', label: 'Help me decide', value: 'method_ideation', icon: '💡' }
                ]);
                break;

            // --- BRANCH: IDEATION (Placeholder) ---
            case 'method_ideation':
                addUserMessage('Help me decide');
                setIsLoading(true);

                // Analyze high margin items
                const highMarginItems = venue?.fullMenu?.filter((item: any) => item.margin_tier === 'High') || [];

                if (highMarginItems.length > 0) {
                    const pickedItem = highMarginItems[Math.floor(Math.random() * highMarginItems.length)];
                    newMessage.text = `I took a look at your menu. Your **${pickedItem.name}** has a great margin. \n\nHow about a Flash Bounty like: "$2 off ${pickedItem.name} for the next hour"?`;
                    setMessages(prev => [...prev, newMessage]);
                    setCurrentBubbles([
                        { id: 'accept_idea', label: 'Sounds good', value: 'accept_ideation_proposal', icon: '✅' },
                        { id: 'manual', label: 'I have a different idea', value: 'method_manual_input', icon: '📝' }
                    ]);
                    // Save for the next step
                    setDraftData({ pickedItem });
                } else {
                    newMessage.text = "I'm still learning your menu! Once I have your food and drink list, I'll be able to suggest high-margin specials. \n\nFor now, please enter the deal manually.";
                    setMessages(prev => [...prev, newMessage]);
                    setTimeout(() => {
                        setOpsState('flash_deal_input');
                        const manualMsg = { ...newMessage, id: Date.now() + '2', text: "So, what's the deal? (e.g., '$5 Pints until 8pm')" };
                        setMessages(prev => [...prev, manualMsg]);
                        setCurrentBubbles([]);
                    }, 1500);
                }
                setIsLoading(false);
                break;

            case 'accept_ideation_proposal':
                const proposal = `$2 off ${draftData.pickedItem?.name} for the next hour`;
                addUserMessage('Sounds good');
                await processAction('SUBMIT_DEAL_TEXT', proposal);
                break;

            // --- SKILL: Social Post ---
            case 'skill_social_post':
                addUserMessage('Social Post');
                setOpsState('social_post_input');
                newMessage.text = "I'm ready to draft. What's the post about? (e.g. 'New IPA on tap', 'Live music at 8pm')";
                setMessages(prev => [...prev, newMessage]);
                setCurrentBubbles([]);
                break;

            case 'SUBMIT_SOCIAL_POST_TEXT':
                if (!payload) return;
                addUserMessage(payload);
                setIsLoading(true);

                // Simple AI-like drafting (Client side for now)
                const draft = `✨ OLYBARS EXCLUSIVE ✨\n\n${payload} at ${venue?.name || 'our place'}! \n\nCome down and join the vibe. 🍻\n\n#OlyBars #Olympia #Nightlife`;

                setDraftData({
                    skill: 'promote_menu_item', // Reusing this for general social drafts
                    params: {
                        item_name: 'Special Update',
                        copy: draft
                    }
                });

                setIsLoading(false);
                setOpsState('confirm_action');
                newMessage.text = `I've drafted this for you:\n\n"${draft}"\n\nSave to your marketing dashboard?`;
                setMessages(prev => [...prev, newMessage]);
                setCurrentBubbles([
                    { id: 'confirm', label: 'Save Draft', value: 'confirm_post', icon: '🚀' },
                    { id: 'gen_img', label: 'Gen Image', value: 'skill_generate_image', icon: '🎨' },
                    { id: 'cancel', label: 'Cancel', value: 'cancel', icon: '❌' }
                ]);
                break;

            // --- SKILL: Email Draft ---
            case 'skill_email_draft':
                addUserMessage('Draft Email');
                setOpsState('email_draft_input');
                newMessage.text = "Who are we emailing, and what's the occasion? (e.g. 'Newsletter to regulars about Saturday trivia')";
                setMessages(prev => [...prev, newMessage]);
                setCurrentBubbles([]);
                break;

            case 'SUBMIT_EMAIL_TEXT':
                if (!payload) return;
                addUserMessage(payload);
                setIsLoading(true);

                const emailDraft = `Subject: Big News from ${venue?.name || 'OlyBars'}! 🍻\n\nHi everyone,\n\n${payload}\n\nWe can't wait to see you there!\n\nCheers,\nThe ${venue?.name || 'OlyBars'} Team`;

                setDraftData({
                    skill: 'draft_email',
                    params: {
                        subject: `Update from ${venue?.name}`,
                        body: emailDraft
                    }
                });

                setIsLoading(false);
                setOpsState('confirm_action');
                newMessage.text = `I've drafted this email:\n\n"${emailDraft}"\n\nSave to your marketing dashboard?`;
                setMessages(prev => [...prev, newMessage]);
                break;

            // --- SKILL: Calendar Post ---
            case 'skill_calendar_post':
                addUserMessage('Calendar Post');
                setOpsState('calendar_post_input');
                newMessage.text = "What event should I add to the community calendar? (e.g. 'St Paddy's Day Bash, March 17th, 6pm')";
                setMessages(prev => [...prev, newMessage]);
                setCurrentBubbles([]);
                break;

            case 'SUBMIT_CALENDAR_TEXT':
                if (!payload) return;
                addUserMessage(payload);
                setIsLoading(true);

                // Mock parsing for now
                setDraftData({
                    skill: 'add_to_calendar',
                    params: {
                        summary: payload,
                        venueId: venue?.id
                    }
                });

                setIsLoading(false);
                setOpsState('confirm_action');
                newMessage.text = `I've prepared this calendar entry:\n\n"${payload}"\n\nPush it to the OlyBars calendar?`;
                setMessages(prev => [...prev, newMessage]);
                break;

            // --- SKILL: Website Content ---
            case 'skill_website_content':
                addUserMessage('Web Content');
                setOpsState('website_content_input');
                newMessage.text = "What page or section are we updating? (e.g. 'About Us section on the homepage')";
                setMessages(prev => [...prev, newMessage]);
                setCurrentBubbles([]);
                break;

            case 'SUBMIT_WEB_TEXT':
                if (!payload) return;
                addUserMessage(payload);
                setIsLoading(true);

                const webDraft = `New Content for ${venue?.name}:\n\n"${payload}"\n\n(Optimized for local SEO and mobile engagement)`;

                setDraftData({
                    skill: 'update_website',
                    params: {
                        content: webDraft
                    }
                });

                setIsLoading(false);
                setOpsState('confirm_action');
                newMessage.text = `Web content drafted:\n\n"${webDraft}"\n\nSave this for your web dev?`;
                setMessages(prev => [...prev, newMessage]);
                break;

            // --- SKILL: Generate Image ---
            case 'skill_generate_image':
                addUserMessage('Gen Image');
                setOpsState('image_gen_purpose');
                newMessage.text = "I'm on it. To get the perfect result, I need a little intel. \n\nWhat is this image for?";
                setMessages(prev => [...prev, newMessage]);
                setCurrentBubbles([
                    { id: '1', label: 'Social Media', value: 'purpose_social', icon: '📱' },
                    { id: '2', label: 'Website', value: 'purpose_web', icon: '🌐' },
                    { id: '3', label: 'Print Flyer', value: 'purpose_print', icon: '📄' },
                    { id: '4', label: 'Member Only', value: 'purpose_exclusive', icon: '👑' }
                ]);
                break;

            case 'SUBMIT_IMAGE_PURPOSE':
                if (!payload) return;
                addUserMessage(payload);
                setDraftData({ ...draftData, purpose: payload });
                setOpsState('image_gen_goal');
                newMessage.text = `Got it, a ${payload}. \n\nWhat's the main goal of this asset?`;
                setMessages(prev => [...prev, newMessage]);
                setCurrentBubbles([
